css:
@-moz-document domain("keylol.com") {
.pg_index, .frame-tab .tb-c {
    background: #edeef3!important;
}
#nav-menu {
    background: #292929!important;
}
#nav-menu > ul > li:after, #nav-menu ul li ul li {
    background: #0e0e0e!important;
}
#nav-logo {
    background: url(https://wangyifang.com/share/img/keylollogo.png) left/contain no-repeat !important;
}
#nav-logo span {
    display: none!important;
}
.index_navi {
    display: none!important;
}
.slideshow span.title{
    background: rgb(0 0 0 / 46%)!important;
    backdrop-filter: saturate(180%) blur(6px)!important;
    border-radius: 0px 0px 5px 5px!important;
}
.slidebar li, .slideshow li, .tab-title, .index_subject_title div {
    border-radius: 5px!important;
    backdrop-filter: saturate(180%) blur(6px)!important;
}
.slidebar li.on {
    color: #FFF!important;
    background: rgb(0 113 199)!important;
}
.index_subject_row {
    padding-top: 45px!important;
}
.frame-1-1-l {
    box-shadow: 0px 3px 10px rgba(0, 0, 0, 0.06)!important;
}
.index_subject_title {
    background: rgb(0 119 185)!important;
}
.index_subject {
    background: rgb(255 255 255)!important;
    border-radius: 13px!important;
    box-shadow: 0px 3px 10px rgba(0, 0, 0, 0.02)!important;
}
.subject_row_detail_text_up a {
    color: rgb(31 31 31)!important;
    font-weight: 600!important;
    font-size: 17px!important;
}
.subject_row_detail_text_down {
    color: #525252!important;
}
.subject_row_detail {
    margin-bottom: 35px!important;
}
header .img-circle {
    border-radius: 0%!important;
}
.tip.tip_4.aimg_tip {
    display:none!important;
}
.t_f, .t_f td {
    font-size: 21px!important;
    line-height: 34px!important;
    letter-spacing: 0.005em!important;
}
.tl .xst {
    font-size: 16px !important;
}
.t_f img {
    margin: 30px 10px 30px 10px !important;
}
.backToTop {
    opacity: 1!important;
    background: url(https://keylol.com/source/plugin/x520_top/template/img/tops.png) center center no-repeat rgb(0 0 0 / 50%)!important;
    border-radius: 40px!important;
    backdrop-filter: saturate(180%) blur(10px)!important;
}
}
