css:
@-moz-document url-prefix("https://forum.vimeworld.com/")
{

.ipsType_sectionTitle{
	font-size: 16px!important;
    line-height: 1!important;
    font-weight: bold!important;
    padding: var(--sp-4) var(--sp-5)!important;
    margin: 0!important;
    background: rgb( var(--theme-section_title) )!important;
    color: rgb( var(--theme-section_title_font) )!important;
    border-top-left-radius: inherit!important;
    border-top-right-radius: inherit!important;
    border-bottom: 1px solid rgba( var(--theme-text_light), 0.15 )!important;
	color: rgba( var(--theme-main_nav_tab_font), 0.6 );
}

body{
	--theme-light_button:47, 49, 54!important;
	--theme-section_title:89, 54, 149!important;
	--box--backgroundColor: rgb( var(--theme-area_background_reset) )!important;
	--theme-area_background_reset:54, 57, 63!important;
	--theme-main_nav:89, 54, 149!important;
	--theme-area_background:47, 49, 54!important;
	--theme-header:32, 34, 37!important;
	--theme-page_background:47, 49, 54!important;
	--theme-main_nav_tab:54, 57, 63!important;
	--theme-important_button:89, 54, 149!important;
	--theme-item_status:89, 54, 149!important;
	--theme-widget_title_bar:54, 57, 63!important;
	--theme-text_light:113, 128, 150!important;
	--theme-text_dark:85, 87, 94!important;
	--theme-text_color: 180 180 180!important;
	--theme-section_title_font:180 180 180!important;
	--theme-widget_title_font:139 137 137!important;
	--theme-primary_button:89, 54, 149!important;
	--theme-main_nav_font:255, 255, 255!important;
	--theme-link:255, 255, 255!important;
	--theme-main_nav_tab_font: 255,255,255!important;
	--theme-header_text:180 180 180!important;
	--theme-footer_text:180 180 180!important;
	--theme-link_hover:139 137 137!important;
	--theme-button_bar:54, 57, 63!important;
	--theme-area_background_light:32, 34, 37!important;
	--theme-light_button_font: 180 180 180!important;
	--theme-very_light_button:47, 49, 54!important;
	--theme-very_light_button_font:180 180 180!important;
	--theme-selected: 54, 57, 63!important;
}

.ipsBreadcrumb {
    font-size: 13px;
    font-weight: 400;
    padding: 12px 0;
    color: rgba( var(--theme-text_light), 0.7 )!important;
	--theme-text_light:180 180 180!important;
}

.ipsAreaBackground_light {
    background: rgb( var(--theme-area_background_light) );
	--theme-area_background_light:54, 57, 63!important;
}

.ipsApp .ipsButton_light {
    background: rgb( var(--theme-light_button) );
	border-color: transparent;
    color: rgb( var(--theme-light_button_font) );
}

.cRatingColumn_on {
	background: var(--positive-light);
	--positive-light:54, 57, 63!important;
}

.cke_button_icon {
    cursor: inherit;
    background-repeat: no-repeat;
    margin-top: 1px;
    width: 16px;
    height: 16px;
    float: left;
    display: inline-block;
    filter: invert(1);
}

.ipsApp .ipsButton_veryLight {
    background: rgb( var(--theme-very_light_button) );
    border-color: rgba( var(--theme-very_light_button_font), 0.075 );
    color: rgb( var(--theme-very_light_button_font) );
}
  
}
