css:
@-moz-document domain("gallery.fitbit.com") {
#fitbitGalleryLogo {
    filter: brightness(7);
}

#root header {
    background: #000;
}

#main {
    background: #1e1e1e;
}

a {
    color:#6495ed;
}

div[class*="TileCard-"] {
    background-color: #1e1e1e;
    border: 1px solid #32373B;
    border-radius: 3px;
}

div[class*="TileCard-"]:hover {
    border: 1px solid #6495ed;
    box-shadow: 0 0 12px 1px #6495ed;
}

div[class*="basic-components__Text"] {
    color: #ccc;
}

div[class*="DiscoveryBannerCarousel__Carousel"] {
    background-color: #1e1e1e;
    border: 1px solid #32373B;
}

div[class*="DiscoveryBannerCarousel__Arrow"] {
    box-shadow: none;
    cursor: pointer;
}

div[class*="SearchBar__Search"] {
    background-color: #1e1e1e;
    border: 1px solid #32373B;
}

div[class*="SearchBar__Search"]:hover {
    border: 1px solid #6495ed;
    box-shadow: 0 0 12px 1px #6495ed;
}

section[class*="basic-components__CalloutSection"] {
    background-color: #1e1e1e;
    border-top: 1px solid #32373B;
    border-bottom: 1px solid #32373B;
    color: #ccc;
}

footer[class*="Footer__FooterBackground"] {
    background-color: #1e1e1e;
}

footer[class*="Footer__FooterBackground"] * {
    color: #ccc !important;
}


input[class*="SearchBar__SearchInput"] {
    background-color: #1e1e1e;
    border: 1px solid #32373B;
    color:#ccc;
}

div[class*="DetailsBanner__Wrapper"] {
    background-color: #1e1e1e;
}

section[class*="DetailsInfo__InfoContainer"] {
    background-color: #1e1e1e;
    border-top: 2px solid #32373B;
}

div[class*="Popup__PopupContainer"] {
    border: 1px solid #6495ed;
    box-shadow: 0 0 12px 1px #6495ed;
    background-color: black;
}



div[class*="PagingNavigation__PageNumberText"],
a[class*="PagingNavigation__PageNumberLink"]{
    background-color: #1e1e1e;
    border: 1px solid #32373B;
    color:#ccc;
}
a[class*="PagingNavigation__PageNumberLink"]:hover {
    border: 1px solid #6495ed;
    box-shadow: 0 0 12px 1px #6495ed;
}

div[class*="PagingNavigation__PageNumberText"]:not(:disabled) {
    color:#6495ed;    
    font-weight:bold;
    font-size:30px;
}


div[class*="Header__SubHeaderWrapper"] {
    background-color: #1e1e1e;
    border-bottom: 1px solid #6495ed;
    box-shadow: 0 0 12px 1px #6495ed;
    color: #ccc;
}


a[class*="Search__FilterButton"] {
    background-color: #1e1e1e;
    border: 1px solid #32373B;
    color:#ccc;
}

a[class*="Search__FilterButton"]:hover {
    border: 1px solid #6495ed;
    box-shadow: 0 0 12px 1px #6495ed;
    color: #6495ed;
}

h2[class*="ResultsBody__ResultsTitle"] {
    color:#ccc;
}
}
