css:
@-moz-document domain("gam.fitbit.com") {
    /* Only Chrome */
    html::-webkit-scrollbar {
        width: 17px;
    }
    html::-webkit-scrollbar-thumb {
        background-color: #040404;
        background-clip: padding-box;
        border: 1px solid #040404;
    }
    html::-webkit-scrollbar-track {
        background-color: #040404;
    }
    ::-webkit-scrollbar-button {
        background-color: #040404;
    }
    /* Only IE */
    html {
        scrollbar-face-color: #6980a8;
        scrollbar-shadow-color: #040404;
        scrollbar-highlight-color: #040404;
    }
    /* Only FireFox */
    html {
        scrollbar-color: #6980a8 #040404;
    }


    body {
        background-color: #18181a;
        color: #ccc;
    }

    .NavContainer--navbar {
        background-color: #000 !important;
    }

    h1,
    h2,
    h3,
    h4,
    label,
    .bp3-heading {
        color: #ccc;
    }


    a,
    a:hover {
        color: #6495ed;
    }

    textarea:disabled,
    input:disabled,
    button.bp3-disabled {
        background: #333 !important;
        color: #8c8c8c !important;
    }

    label.bp3-disabled {
        color: #8c8c8c !important;
    }

    textarea,
    select,
    input,
    .bp3-card,
    #fba-upload-dropzone,
    .bp3-file-upload-input {
        background: #040404 !important;
        border: 1px solid #32373B !important;
        color: #ccc !important;
    }

    .AppDetailsEditor--description textarea:not(:disabled) {
        min-height: 500px;
    }

    .bp3-button {
        background: #000 !important;
        color: #ccc !important;
    }

    .AppOverview--infoTable {
        color: #ccc;
    }

    .MiscDetailsEditor .bp3-popover-wrapper {
        padding: 5px;
    }

    button.bp3-button:not(.bp3-disabled),
    button.bp3-button:not(.bp3-disabled):active {
        border: 1px solid #6495ed;
        border-radius: 2px;
    }

    button.bp3-button:not(.bp3-disabled):hover {
        box-shadow: 0 0 12px 1px #6495ed;
    }

    div.bp3-tab-list div {
        color: #ccc;
    }

    .bp3-tab:not([aria-disabled="true"]):hover,
    .bp3-tab[aria-selected="true"] {
        color: #6495ed;
    }

    .bp3-dialog-header {
        background-color: #18181a;
        border-bottom: 1px solid #6495ed !important;
        padding-bottom: 5px;
    }

    .bp3-dialog-header .bp3-heading {
        color: #ccc !important;
    }

    .ImageManager--uploadDialog.ImageManager--uploadDialog,
    .bp3-dialog {
        background-color: #18181a;
    }

    div[class*="Platform__SupportedPlatformDiv"] {
        background-color: #000;
        border: 1px solid #6495ed;
        border-radius: 5px;
        text-align: center;
    }

    table td,
    table th {
        color: #ccc !important;
    }
    
    #developer-profile-name-wrapper {
        margin-bottom: 60px;
    }
    
    #create-app-button {
        position: absolute;
        top: 115px;
    }
}
