css:
@-moz-document domain("tekstowo.pl") {
::-webkit-scrollbar-track {
             -webkit-box-shadow: inset 0 0 0px rgba(0, 0, 0, 0.3);
             border-radius: 2px;
             background-color: #111;
        }
        html[hide-scrollbar="true"] ::-webkit-scrollbar{display:none}
        ::-webkit-scrollbar {
             width: 8px;
             background-color: #111
        }
        ::-webkit-scrollbar-thumb {
             border-radius: 2px;
             -webkit-box-shadow: inset 0 0 6px rgba(0, 0, 0, .6);
             background: #62ae25 linear-gradient(180deg,#7aba46,#62ae25) repeat-x;
        }

body {
	background: #111;
	color: #d1d5d9;
}
.left-box.bg-green {
    background: #272b24;
    border-color: #272b24;
}
a {
	color: #999;
}
.right-column, .top-row, .big-buttons, .activity-feed .feed-item:after, .pop_content, .pop_content .dialog_content, .pop_content .dialog_buttons {
	background: #111;
}
.card {
    background-color: #111;
    border-color: rgba(255, 255, 255, .125);
}
.topbar {
    background: #292c2f linear-gradient(180deg,#2c2f31,#292c2f) repeat-x;
}
.topmenu>ul li {
	border-color: #111;
}
.form-control {
	background: #111 !important;
	color: #a9b0b7 !important;
	border-color: #4e5257;
}
.activity-feed .feed-item {
	border-color: #24282b;
}
.left-box {
	background: #111;
	border-color: #4e5257;
}
.teledysk-left {
	border-color: #4e5257;
}
.box-przeboje:nth-of-type(2n), .metric tr:nth-of-type(odd) {
    background: #272b24;
}
.alert-warning {
    color: #ce9b06;
    background: #201b0a linear-gradient(180deg,#201a07,#201b0a) repeat-x;
    border-color: #201b0a;
}
.glosowanie, .box-przeboje.sort {
	background: #292c2f;
}
.btn-light2:not(:disabled):not(.disabled).active, .btn-light2:not(:disabled):not(.disabled):active, .show>.btn-light2.dropdown-toggle {
    color: #d1d5d9;
    background-color: #444;
    border-color: #464646;
}
.btn-light2.focus, .btn-light2:focus, .btn-light2:hover {
    color: #d1d5d9;
    background: #323232 linear-gradient(180deg,#393939,#323232) repeat-x;
    border-color: #444;
}
.btn-light2 {
    color: #d1d5d9;
    background: #262626 linear-gradient(180deg,#292929,#262626) repeat-x;
    border-color: #262626;
}
.metric {
	border-color: #444;
}
.pokaz-rev .icon, .zdjecie.teledysk .icon {
	filter: invert(.935);
}
.revisions, .popularni {
	background: #18191a;
}
div#song_revisions > p {
	padding-left: 9px;
}
.box-content {
	background: #111;
	border-color: #444;
}
.to-move .popularny img {
	background: #111;
	border-color: #4d4d4d;
}
.top5 img, .user-info .avatar {
	border-color: #4d4d4d;
}
.page-link:hover {
	background: #191c1f;
	border-color: #3e4144;
}
.page-link {
	background: #111;
	border-color: #3e4144;
}
.nav-tabs .nav-item.show .nav-link, .nav-tabs .nav-item.show .navbar-nav>li>a, .nav-tabs .nav-link.active, .nav-tabs .navbar-nav>li.show .nav-link, .nav-tabs .navbar-nav>li.show .navbar-nav>li>a, .nav-tabs .navbar-nav>li>a.active {
    color: #a9b0b7;
    background-color: #111;
    border-color: #3e4144 #3e4144 #111;
}
.nav-tabs .nav-link:focus, .nav-tabs .nav-link:hover, .nav-tabs .navbar-nav>li>a:focus, .nav-tabs .navbar-nav>li>a:hover {
    border-color: #292c2f #292c2f #3e4144;
}
.nav-tabs, .table thead th {
	border-color: #3e4144;
}
.table {
	color: #d1d5d9;
}
.table-hover tbody tr:hover {
    color: #d1d5d9;
    background-color: rgba(255, 255, 255, .075);
}
button, input[type="button"], input[type="submit"] {
	background: linear-gradient(180deg, #111, #222);
	border: 1px solid #232323;
	color: #fff;
}
button:hover, input[type="button"]:hover, input[type="submit"]:hover {
	background: linear-gradient(180deg, #1b2225, #323a41);
}
button:active, input[type="button"]:active, button:focus, input[type="button"]:focus, input[type="submit"]:focus, input[type="submit"]:active {
	background: linear-gradient(180deg, #253338, #3c4750);
}
.activity-feed .feed-item .date {
    color: #7c8693;
}
[style="color: rgb(102, 102, 102); font-family: Arial, Verdana, Tahoma; font-size: 1.1em; line-height: 1.3em; text-align: justify; "], [style="color: rgb(102 102 102);font-family: Arial, Verdana, Tahoma;font-size: 1.1em;line-height: 1.3em;text-align: justify;"], [style="margin: 5px 0px 5px 20px; padding: 0px; border: 0px; font-size: 1.1em; font-family: Arial, Verdana, Tahoma; vertical-align: baseline; line-height: 1.3em; text-align: justify; color: rgb(102, 102, 102); "], [style="margin-top: 5px; margin-right: 0px; margin-bottom: 5px; margin-left: 0px; padding-top: 0px; padding-right: 0px; padding-bottom: 0px; padding-left: 0px; border-top-width: 0px; border-right-width: 0px; border-bottom-width: 0px; border-left-width: 0px; border-style: initial; border-color: initial; border-image: initial; font-size: 1.1em; font-family: Arial, Verdana, Tahoma; vertical-align: baseline; line-height: 1.3em; text-align: justify; color: rgb(102, 102, 102); "] {
	color: #999 !important;
}
.login-wrap {
    border-color: #3e4144;
    background: #191919;
}
.custom-control-label:before {
    background-color: #111;
    border-color: #5d6368;
}
.custom-control-input:not(:disabled):active~.custom-control-label:before {
	color: #111;
}
.custom-checkbox .custom-control-input:checked~.custom-control-label:after {
	filter: invert(.935);
}
.btn:hover, .search-box .inner .search-form .submit:hover {
	color: #d1d5d9;
}
.input-group-text {
	background: #292c2f;
	border-color: #3e4144;
	color: #a9b0b7;
}
.soundtracks .page-link:hover {
	background: #292c2f;
	border-color: #3e4144;
}
.soundtracks .page-link {
	background: #111;
	border-color: #3e4144;
	color: #378aff;
}
.alert-danger {
    color: #d73544;
    background: #170d0e linear-gradient(180deg,#1a0e0f,#170d0e) repeat-x;
    border-color: #150c0d;
}
.soundtracks .left-box.bg-green {
    background: #3d4146;
    border-color: #3d4146;
}
.soundtracks .left-box h4, .soundtracks .wykonawca a, .top-tracks, .soundtracks a:hover {
    color: #378aff;
}
.wykonawca img {
	border-color: #4e4e4e;
}
.top-tracks, h3.top-track {
	border-color: #282828;
}
.top-tracks .views, .top-tracks .votes {
	filter: invert(.935);
}
.top-tracks img {
	background-image: none;
}
[style="padding-right: 10px; border-right: 1px solid gray;"], [style="margin-top: 1px; font-weight: bold; border-bottom: 1px solid gray; font-size: 1.6em;"] {
	border-color: #777 !important;
}
[style="margin: 15px 0 0 0; color: #000;"] {
	color: #fff !important;
}
.komentarz .bar {
	background-color: #171717;
	color: #666;
}
.comm_show_more {
	border-color: #252525;
}
.komentarz img.avatar {
	border-color: #4d4d4d;
}
[src="/static/images/user_no_avatar.gif"] {
	filter: invert(.935);
	border-color: #cdcdcd !important;
}
}
