css:
@-moz-document domain(animeflv.net) {
@media (min-width: 1200px) {
.Container {
    max-width: 100%;
}
}

@media (min-width: 990px) {
.CapiTcn:before {
    height: 700px;
}
}

@media (max-width: 950px) {
.CapiTcn:before {
    height: 450px;
}
}

@media (min-width: 1020px){
.CpCnC {
    position: relative;
    right: 0;
    top: 0;
    width: 300px;
    transition: .2s;
}
.CpCn {
    padding-right: 0px;
    transition: .2s;
}
}
body {
    background: #2f353a;
}

.CpCnA .CapiTop .Title {
    color: #fff;
}
a, .Title {
    color: #838d95;
}

.ShrCnB .Title {
    color: #adb9c4;
}
.ShrCnB p {
    color: #bbc3ca;
}
.CapNv {
    background-color: #485155;
}

.Title.Page h1, .Title.Page h2, .Title.Page h3, .Title.Page h4, .Title.Page h5, .Title.Page h6 {
    color: #c5cfd7;
}
.Anime.alt>a>.Title {
    color: #e2e7ea;

}
}
