/* ==UserStyle==
@name         Bazos Dark | modernized
@description  Dark modernized theme for bazos.sk
@namespace    https://github.com/michael-s-dev/bazos-dark-modernized/
@version      0.5.1
@author       Michael
@homepageURL https://github.com/michael-s-dev/bazos-dark-modernized/
==/UserStyle== */

@-moz-document domain("bazos.sk") {

*{  font-family: 'Roboto', sans-serif !important; }
body , html {
    background-color: #222 !important;
    font-size:15px;
    margin:0;

}

.fucking-eu-cookies,
.fucking-priority {
    display: none !important;
}
.listalogol img {
    display: none;
}
.listalogol a::after {
    content: 'Bazoš';
    color: #EEE;
    font-size: 40px;
}
.listalogol a::before {
    content: '@)';
    color: #FF6600;
    font-size: 40px;
}
.listah {
    background-color: #141312;
    margin: 3px;
    border: none;
    padding: 18px;
    border-radius: 1px;
    position: fixed;
    top: -3px;
    left: -3px;
    right: -3px;
    z-index: 1000000;
}
.listah::before {
    content: 'Filter:';
    font-size: 20px;
    font-weight: 700;
    position: absolute;
}
body,
td,
div,
.p,
a {
    color: #CCC;
    font-size: 15px;
}
.nadpisnahlavni A,
.nadpisnahlavni A:hover {
    font-size: 25px;
    font-weight: bold;
    line-height: 56px;
    color: #aaa;
}
.icontblcell1,
.icontblcell2 {
    display: table-cell;
    padding: 8px;
    border-right: none;
    width: 25%;
    background: #0606062C;
    border: 20px solid #222;
    cursor: pointer;
    transition: all 0.3s;
}
.icontblcell1:hover,
.icontblcell2:hover {
    transform: scale(1.05);
}

.icontblcell1:hover span a,
.icontblcell2:hover span a{
    color:#4a6 !important;
}
.pridati {
    background-color: #4a6;
    border: none;
    padding: 9px 17px;
    border-radius: 3px;
    color: #FFF;
}

.pridati:before{
    content:'+';
    padding-right:7px;
    font-size:1.1rem;
    font-weight:400;
}

.listalogo {
    padding: 110px 0px 35px 0px;
}
input,
select,
textarea {
    background: #1D1D1D;
    color: #CCC;
    border: none;
}
/*search*/
.listal {
    border-right: none !important;
    border: none;
}
.nadpismenu {
    background-color: #141312;
    border: none;
}
.barvalmenu {
    background-color: #1D1D1D;
    padding: 10px;
    border: none;
}
.listal + td > div:first-child,
td [width="50%"] {
    display: none !important;
}
table.inzeraty {
    border: none
}
td[width='*'] > br {
    display: none !important
}
td[width='*'] > div > b {
    line-height: 59px;
    color: #722
}
.inzeraty:hover {
    background-color: #1a1a1a;
}
.nadpis a {
    text-decoration: none;
    font-size:1.06rem;
}
.popis {
    padding-top: 18px;
    padding-right: 10px;
    padding-bottom: 10px;
}
.obrazek {
    float: left;
    border: 1px solid Black;
    margin: 24px 33px 10px 0px;
}
.inzeraty {
    padding: 20px;
    margin-bottom: 20px;
    background-color: #1D1D1D;
}
a {
    visibility: visible !important;
    text-decoration: none !important;
}
.inzeraty td[width='63%'] {
    width: 63%;
}
.inzeraty td[width='63%'] + td[width='15%'],
.inzeraty td[width='63%'] + td[width='15%'] + td[width='15%'] {
    width: 9%;
}
.cisla {
    background-color: #141312;
    border: none;
}
.strankovani a,
.strankovani b {
    font-size: 20px;
    padding: 8px;
    border-radius: 2px;
    text-decoration: none;
}
.strankovani a:hover {
    background-color: #1D1D1D;
}
.cena {
    color: #272;
}

input , select {
    padding:5px 7px !important;
    margin:3px 4px !important;
}

button,
input[type="submit"] {
    background: transparent;
    padding: 7px 26px !important;
    color: #DDD;
    text-transform: uppercase;
    font-weight: 400;
    box-shadow: 0px 0px 8px #111;
    margin: 0px 10px;
    cursor: pointer;
    border:1px solid #EEE;
    letter-spacing:0.1rem;
}

a[href="/pridat-inzerat.php"] {
    position: fixed;
    top: 22px;
    right: 20px;
    z-index: 9999999999999999999;
}

.listainzerat {
    background-color: #141312;
    border: none;
    padding:15px 10px;
}

.listainzerat + button,
.gallerylist,
.gallerylista,
.nadpiskategorie,
.nadpiskat {
    display: none;
}

.listainzerat td[width='66%'] {

    width: 59%;
}

.listainzerat td[width='15%'],
.listainzerat td[width='14%'] {

    width: 6.5%;
}

.listadvlevo,
.listadvpravo {
    border-right: none;
    border-top: 2px solid #131211;
    padding-top: 30px;
}
input[value='Ohodnotiť užívateľa'] {
    display: none;
}
td.barevne {
    border: none;
    background-color: #1D1D1D;
    padding: 20px;
}
.ztop[title]{
  font-size:0;
}

.ztop[title]:after{
    content:' Platená reklama ';
    color: #973;
    font-size:13px;
}

.listal + td input,
.listal + td select,
.listal + td textarea {
    background-color: #333;
    margin: 5px 0;
}
.bodyr input,
.bodyr select,
.bodyr textarea {
    background-color: #333;
    margin: 5px 0;
}



.ui-state-default,
.ui-widget-content .ui-state-default,
.ui-widget-header .ui-state-default {
    background-color: #131211 !important;
}

.plupload_container,
.ui-widget-header {
    border: none !important;
    background-color: #333 !important;
}
.plupload_button {
    border: none !important;
    color: #CCC !important;
}
.plupload_button:hover {
    border: none !important;
    color: #CCC !important;
    background-color: #222 !important;
}
.bodyr {
    background-color: #1d1d1d !important;
}

.inzeratynadpis img , table.inzeraty::before{
float: left;
height: 100% !important;
border:none !important;
position: absolute;
width: 198px;
top: 0;
left: 0;
margin: 0 ;
max-width:inherit !important;
