    display:block;
    padding:5px 80px;
    padding-top:20px;
    margin-top:40px;
}

div[align="left"] ~ div[align="left"] {
    padding-top:0px;
        margin:0px;
}

div[align="left"]:last-child{
    padding-bottom:20px;
}



.sirka>a , .sirka {font-size:0;}
.sirka {
    
    max-width:100000px !important;
}

.sirka>.flexmain , .drobky  , .sirka>.icontblrow{
    
    max-width:1500px !important;
    margin:0 auto;
}

.listalogor{
    
    background: linear-gradient(to right, #141e30, #243b55);

}

.listalogor{

    display:flex;
    margin:0 auto;
    margin-bottom:75px;
    margin-top:75px;
    padding:30px;
    align-items:center;
}

.act{
    background-color:#222;
    color:#EEE;
}
.act:hover{
    background-color:#4a6;
}

.flickity-page-dots{
   bottom:5px;
}

