css:
@-moz-document domain("web-msort") {

    body {
        background: none;
        background-color: #323234 !important;
        color: #a2a2a1 !important;
    }

    table[rules="All"] td,
    .style9 table,
    .linksmenu,
    #pnl_Insert,
    .hist_div_srch {
        border: 1px solid #121222 !important;
        background-color: #323234 !important;
        color: #a2a2a1 !important;
    }

    table[title="Календарь"] th {
        border: 1px solid #a2a2a1 !important;
        background-color: #424244 !important;
        color: #a2a2a1 !important;
    }

    table[title="Календарь"] td {
        border: 1px solid #a2a2a1 !important;
        background-color: #323234 !important;
        color: #a2a2a1 !important;
    }

    .linksmenu table td,
    .style9 table table,
    span {
        border: 0px !important;
    }

    table[rules="all"] table td {
        border: 0px !important;
    }

    table[rules="all"] table td a {
        color: #ebff00 !important;
        font-size: 16px;
        text-decoration: none;
    }

    table[rules="all"] table td span {
        color: #ebff00 !important;
        font-size: 16px;
        text-decoration: underline;
        font-weight: bold;
    }

    input[type="text"],
    textarea,
    select {
        background-color: #323234 !important;
        color: #a2a2a1 !important;
        text-align: left !important;
        border-style: inset !important;
        border-radius: 5px !important;
    }

    textarea[id$="edit"],
    textarea[id$="Edit"],
    input[type="Text"][title="Статус"],
    .style9 table table input {
        background-color: #312e51 !important;
        color: #a2a2a1 !important;
    }

    a,
    .bold_txt {
        color: #a2a2a1 !important;
        font-weight: bold !important;
    }

    #templatemo_menu ul li a {
        border: 1px solid #121222 !important;
    }

    #logo {
        color: #a2a2a1 !important;
    }

    input[type="submit"] {
        background-color: #312e51 !important;
        color: #a2a2a1 !important;
    }

    .PopupTitle {
        background-color: #312e51 !important;
        color: #a2a2a1 !important;
        padding-left: 10px;
    }

    .hist_div_btn_add input,
    #DeviceHistoryGrid input[value='Добавить'] {
        background-color: #024400 !important;
        color: #a2a2a1 !important;
        font-weight: bold !important;
        font-size: 15px;
    }

    #DeviceHistoryGrid div {
        display: block !important;
    }

    .font_red_bold {
        color: #c90000 !important;
        font-weight: bold !important;
    }

    table[rules="All"] tr:first-child {
        background-color: #424244 !important;
        color: #a2a2a1 !important;
    }

    table[rules="All"] tr[style="color:White;background-color:#669999;font-weight:bold;"] td {
        background-color: #312e51 !important;
    }

    table[rules="All"] tr[style="color:#000066;background-color:#FFCC66;"] td {
        background-color: #46233c !important;
    }

    table[rules="All"] th {
        border: 1px solid #121222 !important;
    }


    #templatemo_menu_wrapper,
    #templatemo_footer,
    #templatemo_footer_wrapper {
        background-image: none !important;
        background-color: #323234 !important;
        color: #a2a2a1 !important;
    }

    #templatemo_header,
    #templatemo_header_wrapper {
        display: none !important;
    }

    img.style4 {
        mix-blend-mode: multiply;
    }
}
