css:
@-moz-document domain("wish.com") {
/* Home page */
.kZqYkL{ background-color: #202020 !important;}

/* Navbar */
.fkGRIh{ background-color: #202020 !important;}
.hxGaay, .eBaZqN{ background-color: grey !important;}

/* Categories sidebar */
.ejUbfg{ background-color: #202020 !important;}
.hPWhsk, .gePeWF{ color: #fff !important;}

/* Referral/Cart sidebar */
.jshQqj, .jFXYuX, .fJYgbV{ background-color: #202020 !important;}
.jFXYuX, .fSkyel{ color: #fff !important;}
}

@-moz-document url-prefix("https://www.wish.com/search/") {
/* Search */

.dbNTD { background-color: #202020 !important;}


.bqTbXB{ color: #fff !important;}
}

@-moz-document regexp("https://www.wish.com/search/.+/product/.+"), regexp("https://www.wish.com/feed/tabbed_feed_latest/product/.+"), regexp("https://www.wish.com/feed/brand__tab/product/.+"), regexp("https://www.wish.com/feed/recently_viewed__tab/product/.+"), regexp("https://www.wish.com/feed/blitz_buy__tab/product/.+"), regexp("https://www.wish.com/feed/pickup__tab/product/.+"), regexp("https://www.wish.com/feed/express__tab/product/.+"), regexp("https://www.wish.com/feed/tag_.+"), regexp("https://www.wish.com/wishlist/.+/product/.+"), regexp("https://www.wish.com/product/.+") {
/* Product modal */
.eryARl, .iZBSuX{ background-color: #202020 !important;}

.eryARl, .Timys, .ifHoGS, .kKtlir, .hZYnJL, .dvksrQ, .cUiLwR, .ewsqYD, .gUIURw, .bcCZXo, .bxGglp, .iPVMNR, .gOeIqn, .ccoAOE, .jPDQvO, .knsNHu, .ehaKtq, .gtdqXi, .fvWkso, .boxnOU, .uSTZX, .dpruKW, .FkMPp, .zNrbd, .jSdbXF, .ftgUYz, .GJXjz, .bhJhkg
{ 
    color: #fff !important;
}

.hLgcCh svg g{ stroke: #fff !important;}
}

@-moz-document regexp("https://www.wish.com/product/.+") {
/* Product oage */
.kQNixj, .ikLNTb, .hdMXmQ{ background-color: #202020 !important;}

.kNMcGe, .itOPqw, .kPueQS, .foVdic, .boGBfo, .iNrTDd, .zNrbd, .gFNweu, .kFxYvg, .eKZopX, .fRsSGC
{ 
    color: #fff !important;
}
}

@-moz-document url("https://www.wish.com/feed/blitz_buy__tab") {
/* Blitz Buy */
.erOQbY{ background-color: #202020 !important;}
}

@-moz-document url("https://www.wish.com/cart") {
/* Cart */
.BtMiF, html{ background-color: #202020 !important;}
.fKjvEt, .jQjFrX, .chZUxL, .ihMHmS, .cSRYwq, .lfrUMW, .khiFAC{ color: #fff !important;}
.jvVaXU svg g g{ fill: #fff !important;}
}

@-moz-document url("https://www.wish.com/wishlist"), url("https://www.wish.com/profile"), url-prefix("https://www.wish.com/profile?uid="), regexp("https://www.wish.com/wishlist/.+") {
/* Wishlist */
html, .fajsNi, .faNMNk, .gnAJNR{ background-color: #202020 !important;}
.jvvBeL, .ZmYQj, .dsFKPR, .hsVwNH, .bpVRwT{ color: #fff !important;}
}

@-moz-document url-prefix("https://www.wish.com/settings") {
/* Settings */
html, .gVoJZ{ background-color: #202020 !important;}
.fPFdPq, .dbGuMg, .cOhjZq, .cOhjZq, .iYKjGf, .bvgUBP, .jLKevN, .foAci, .fgUccn, .hNiZZv, .fsgBGz, .cLjpiI, .tKDol{ color: #fff !important;}
}

@-moz-document regexp("https://www.wish.com/purchase-confirmation/.+") {
/* Purchase confirmation */
.fikjGN{ background-color: #202020 !important;}
.hwEqcP, .bJXuHL, .hPpnpv, .YhFyf, .azSzH, .hAjpT, .gWmNtb, .hisLyr, .bhhMQG{ color: #fff !important;}
}

@-moz-document url-prefix("https://www.wish.com/rewards") {
/* Rewards */
.iIYQUZ, .fDpRum{ background-color: #202020 !important;}
.dllHIa, .MjYHf, .beJasy, .fRCjwv, .gSJBVk, .cgydNA, .cNSPwL, .jOMKqC, .hKMWQv{ color: #fff !important;}
}

@-moz-document url-prefix("https://www.wish.com/transaction") {
/* Transaction */
.iIYQUZ, .iIYRpU, .ggHvuC, .bfljvW{ background-color: #202020 !important;}
.dEieuI, .YhFyf, .dUaSWZ, .ccwuFf, .cQyuCd, .hwEqcP, .azSzH, .ePiLZZ, .jgqNfA, .hAVebK, .gTwLGe, .kPHtOI, .ieQteW, .gedMvI, .kllFFj, .dSZyTP, .eFUAPE, .hRKfAC, .jkEXKt, .hiwmyf{ color: #fff !important;}
.hdYUjP svg path{ stroke: #fff !important}
}

@-moz-document regexp("https://www.wish.com/transaction/.+/product/.+") {
/* Transaction product delivery tracking */
.ePiLZZ, .cQyuCd, .kKgvwL, .cSwErR, .bjYkVe, .dDNcdp, .fwFTvW{ color: #fff !important;}
}

@-moz-document url-prefix("https://www.wish.com/refer") {
/* Refer */
.iIYQUZ, html, .BSwej{ background-color: #202020 !important;}
.cZytFc, .fgyrNc, .dLzduR, .jioDJE, .kqGydD{color: #fff !important}
}

@-moz-document url-prefix("https://www.wish.com/daily-login-bonus") {
/* Daily login bonus */
html{ background-color: #202020 !important;}
.guEQBP, .irRsxq, .lnxYGE{color: #fff !important}
}

@-moz-document url("https://www.wish.com/notifications") {
/* Notifications */
html, .kaOFgX{ background-color: #202020 !important;}
.dKjtrd, .bRYDsB, .ieSCBG{color: #fff !important}
}
