    font-size: 14px;
    line-height: 22px;
    flex: 1 1 auto;
    height: auto;
    min-width: 0px;
    margin: 0px -9px;
    padding: 9px 8px;
        padding-right: 8px;
        padding-left: 8px;
    border: medium none;
    border-radius: 4px;
    color: rgba(255, 255, 255, 0.68) !important;
    background-color: rgba(255, 255, 255, 0.12);
    transition: background-color 0.2s ease 0s, color 0.2s ease 0s;}
.gkpNOe {
    pointer-events: auto;
