    .ffUGLk, .hMfrcr {color: rgba(255, 255, 255, 0.68);}
    .cLtKim {color: rgba(255, 255, 255, 0.36);}
    .gaxotS {color: rgba(255, 255, 255, 0.28);}
.jVALri {
    display: inline-block;
    min-width: 64px;
    line-height: 22px;
    margin: 0px;
    padding: 9px 24px;
    border: medium none;
    border-radius: 100vw;
    font-size: 14px;
    font-weight: bold;
    cursor: pointer;
    transition: background-color 0.2s ease 0s, color 0.2s ease 0s;
    color: rgba(255, 255, 255, 0.68);
    background-color: rgba(255, 255, 255, 0.12);}
    .dkLGej {
        pointer-events: auto;
        visibility: visible;
        position: absolute;
        left: 1033.2px;
        top: 56px;
        z-index: 11;
        border-radius: 8px;
        border: 1px solid rgba(255, 255, 255, 0.12);
        overflow: hidden;
        background-color: rgb(31, 31, 31);
        max-height: calc(-80px + 100vh);}
.bQUtkV, .fnwGms, .ekybIy.ekybIy, .gYzPTN, .iGapVa, .bQOJEY {color: rgba(255, 255, 255, 0.36);}
.wcsTA {color: rgba(255, 255, 255, 0.68);}

#wrapper {
    margin: 0px auto;
    width: 1224px;}
    .layout-body, .layout-a, .layout-b, .layout-column-2 {
        width: 100% !important;}
    ._unit, .footer {
        border-color: transparent;
        background-color: transparent;}
.self {
    color: rgba(255, 255, 255, 0.68) !important;}
.count-badge {
    display: inline-flex;
    -moz-box-pack: center;
    justify-content: center;
    -moz-box-align: center;
    align-items: center;
    flex: 0 0 auto;
    height: 20px;
    min-width: 20px;
    color: rgb(255, 255, 255);
    font-weight: bold;
    padding: 0px 6px;
    background: rgba(0, 0, 0, 0.32) none repeat scroll 0% 0%;
    border-radius: 10px;
    font-size: 10px;
    line-height: 10px;}

div._unit {display: flex;flex-direction: column;}
.column-title {margin: 24px 0;}
    .column-menu {
        order: -1;
        background-color: transparent;}
    .column-menu .menu-items {padding: 0 10px 0 0;}
    .column-menu .menu-items + .menu-items {padding: 0 0 0 10px;}
        .column-menu li {
