.pager-container {margin-bottom: 36px;}
.pager-container + .menu-items {
    position: absolute;
    top: -53px;
    right: 0;
    left: auto;
    z-index: 9;}
    .menu-items a {
        color: rgba(255, 255, 255, 0.12);}
    .menu-items .current {
        color: rgba(255, 255, 255, 0.36);
        text-decoration: none;}
.pager-container a, .pager-container .current {
    display: inline-flex;
    justify-content: center;
    align-items: center;
    width: 40px;
    height: 40px;
    padding: 0;
    transition: color .2s;
    font-size: 14px;
    font-weight: 700;
    color: #999;}
.pager-container .current {
    background: #333;
    border-radius: 50%;
    color: #fff;}
.pager-container a:hover {
    text-decoration: none;}
a[rel="prev"]._button, a[rel="next"]._button {
    width: 40px;
    margin: 0;
    background: transparent;
    border-color: rgba(255, 255, 255, 0.12);}
.page-list li + li {border-left: 0;}


    .manage-unit .image-item .input-container input {
        background: rgb(31, 31, 31);
        appearance: none;
        position: absolute;
        top: -1px;
        left: 0;
        height: 20px;
        width: 20px;
        vertical-align: -0.8rem;
        transition:all .05s ease-out 0s;
        color: #fff;
        cursor: pointer;
        display: inline-block;
        margin: .4rem;
        outline: none;
        border-radius: 10%;
        z-index: 99;}
    .manage-unit .image-item .input-container input:before,.manage-unit .image-item .input-container input:after {
        position: absolute;
        content: "";
        background: #fff;
        transition: all .1s ease-in-out;}
    .manage-unit .image-item .input-container input:before {
        left: 2px;
        top: 6px;
        width: 0;
        height: 2px;
        transform: rotate(45deg);}
    .manage-unit .image-item .input-container input:after {
        right: 9px;
        bottom: 3px;
        width: 2px;
        height: 0;
        transform: rotate(40deg);
        transition-delay: .1s;}
    .manage-unit .image-item .input-container input:checked:before {
        left: 1px;
        top: 10px;
        width: 6px;
        height: 2px;}
    .manage-unit .image-item .input-container input:checked:after {
        right: 5px;
        bottom: 1px;
        width: 2px;
        height: 14px;}
    .manage-unit .image-item .input-container input:indeterminate:before,.manage-unit .image-item .input-container input:indeterminate:after {
        width: 7px;
        height: 2px;
        transform: rotate(0);}
    .manage-unit .image-item .input-container input:indeterminate:before {
        left: 1px;
        top: 7px;}
    .manage-unit .image-item .input-container input:indeterminate:after {
        right: 1px;
        bottom: 7px;}
    .manage-unit .image-item .input-container input {border: 2px solid #2196f3;}
    .manage-unit .image-item .input-container input:checked,.manage-unit .image-item .input-container input:indeterminate {background: #2196f3;}

._image-items {
    margin: 0;
    display: flex;
    flex-wrap: wrap;
    justify-content: space-between;}
.image-item > a {color: rgba(255, 255, 255) !important;}
.image-item > a:visited, .image-item .user {color: #B6B6B6 !important;}
.image-item .user:visited, .image-item ._illust-series-title {color: #6E6E6E !important;}
.msgbox_bottom {
    background-color: rgba(148, 148, 148, 0.192) !important;
    border-color: transparent !important;}
    .msgbox_bottom > ul {
        margin-left: 24px;
        display: inline-block;
        width: auto;}
#add_tag {width: 240px !important;}



.novel-right-contents .thumbnail-menu {background-color: transparent;}
.layout-a .layout-column-1, .comic-hot-works, .list-b .bookmark-box, .edit-bookmark, .banner, .msgbox_bottom > br, .autopagerize_page_separator, .column-header {display: none;}
}

