@-moz-document domain("timesonline.co.uk") {

a[href*="comments-form"], div.comment-article, div.puff-top, div[class*="related-attachements"], embed, iframe, li.delicious, li.digg, li.email, li.fark, li.yahoo, li[class="icon blog"], li[class="icon your-view"], object, #google-ad-art, #grid-column4, #region-column1-layout2 > div[class^="padding-bottom-"], #region-column2-layout2, #region-footer, #region-top-ad, .header-quote-container { display: none !important; }

div[class="float-left width-286"], div[class="width-585 bg-d9d9d9 float-left"], #region-column1-layout0, #region-column1-layout2, #region-column1and2-container-layout2 { width: 100% !important; }

