css:
@-moz-document url-prefix("https://pawn-wiki.ru/") {
body {
    background: #0a0a0a url(https://lh3.googleusercontent.com/proxy/POU51dsDaLhoL3FAplIiyV_FueRnOdeiLFesG_zwKIvlLte2rlNkjxdp4EVpvbaRqHZLIFPL8pab8hfI2dbQrG8RWzSfGyYAFb8M4Y9tPApYyEZqQXoKTmVIvyguNyUPcKFuRJ64ax80EufUznBR) repeat-x 50% 0;
    color: #1c2837;
    padding-bottom: 10px;
    font: normal 12px tahoma, arial, verdana, sans-serif;
    position: relative;
}
.maintitle_base {
    background: url(https://armandrp.000webhostapp.com/images/pawnwiki/maintitle.jpg) repeat-x 0 0;
    min-height: 47px;
}
body .maintitle {
    text-shadow: #000 0px -1px 0px;
    background: #000 url(https://lh3.googleusercontent.com/proxy/POU51dsDaLhoL3FAplIiyV_FueRnOdeiLFesG_zwKIvlLte2rlNkjxdp4EVpvbaRqHZLIFPL8pab8hfI2dbQrG8RWzSfGyYAFb8M4Y9tPApYyEZqQXoKTmVIvyguNyUPcKFuRJ64ax80EufUznBR) repeat-x left 50%;
    color: #fff;
}
body {
    background: #0a0a0a url(https://lh3.googleusercontent.com/proxy/POU51dsDaLhoL3FAplIiyV_FueRnOdeiLFesG_zwKIvlLte2rlNkjxdp4EVpvbaRqHZLIFPL8pab8hfI2dbQrG8RWzSfGyYAFb8M4Y9tPApYyEZqQXoKTmVIvyguNyUPcKFuRJ64ax80EufUznBR) repeat-x 50% 0;
    color: #fff;
    padding-bottom: 10px;
    font: normal 12px tahoma, arial, verdana, sans-serif;
    position: relative;
}
#secondary_nav {
    color: #fff;
    font-size: 0.9em;
    border-bottom: 1px solid #5b5b5b;
    font-weight: bold;
    height: 37px;
    text-shadow: #617b98 0px -1px 0px;
    background: #5b5b5b;
    clear: both;
}
.stats_container #stats {
    background-color: #3c3c3c;
    border-left: 1px solid #303131;
}
a {
    color: #fff;
}
#index_stats .hentry,
.row1 td,
.row2 td,
#help_topics li {
    border-bottom: 1px solid #2d2d2d;
    border-top: 1px solid #2d2d2d;
}
.input_submit {
    background-color: #2b2b2b;
    color: #fff;
    border: 1px solid #171717;
}
.input_submit.alt {
    background-color: #595959;
    border: 1px solid #444;
    color: #fff;
}
.input_text {
    background: #000 url(https://armandrp.000webhostapp.com/images/pawnwiki/input_text.png) repeat-x;
    font-size: 0.9em;
    font-family: arial, verdana, tahoma, sans-serif;
    border: 1px solid #bdbdbd;
    padding: 4px;
}
.row2 .altrow,
.row2.altrow,
ul.topic_moderation {
    background-color: #2d2d2d;
}
.row2 .altrow,
.row1.altrow,
.personal_info,
.general_box {
    background-color: #2d2d2d;
}
.row1 .altrow,
.row1.altrow,
.personal_info,
.general_box {
    background-color: #2d2d2d;
}
.row1,
.row1 td,
.post_block.row1,
.no_messages {
    background-color: #2d2d2d;
}
.row2,
.row2 td,
.post_block.row2,
.message {
    background-color: #2d2d2d;
}
.header {
    background: #444343;
    color: #fff;
    text-shadow: #607fa2 0px -1px 0px;
}
.general_box h3 {
    background: #484848;
    color: #fff;
}
.header th {
    border-bottom: 1px solid #444;
    background: url(https://) repeat-x 0 100%;
}
#index_stats .sidebar_content {
    background: #2b2b2b;
    overflow: hidden;
}
#secondary_nav li.breadcrumb a span {
    display: block;
    padding-right: 14px;
    padding-left: 5px;
    background: url(https://armandrp.000webhostapp.com/images/pawnwiki/secondary_nav_link.gif) no-repeat 100% 0;
}
.header_right {
    background: url(https://armandrp.000webhostapp.com/images/pawnwiki/cat_right.gif) no-repeat 100% 0;
    min-height: 47px;
}
.header_left {
    background: url(https://armandrp.000webhostapp.com/images/pawnwiki/cat_left.gif) no-repeat 0 0;
    min-height: 47px;
}
.box_bl {
    background: url(https://armandrp.000webhostapp.com/images/pawnwiki/box_footer.gif) no-repeat 0 -8px;
    height: 8px;
}
.box_br {
    background: url(https://armandrp.000webhostapp.com/images/pawnwiki/box_footer.gif) no-repeat 100% -16px;
    height: 8px;
}
.box_l {
    background: #242424 url(https://armandrp.000webhostapp.com/images/pawnwiki/box_sides.gif) repeat-y 0 0;
}
.box_r {
    background: url(https://armandrp.000webhostapp.com/images/pawnwiki/box_sides.gif) repeat-y 100% 0;
    padding: 0px 8px;
}
.box_b {
    background: #1e4371 url(https://armandrp.000webhostapp.com/images/pawnwiki/box_footer.gif) repeat-x 0 0;
    height: 8px;
}
}

