css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("howstuffworks.com") {

  .relatedAds,
  #googleAdBottom,
  .adv,
  .adBoxSingle,
  div[style="margin: 0 -10px 10px -14px; width: 619px; border: solid 1px #d2d2d2; border-left:none; border-right:none; padding: 10px;"],
  #smToolBar,
  #smToolBarSpacer,
  #footer p,
  #subChannelNav li.divider,
  .adBotBanner,
  .smIcons,
  .ad,
  .imageBox br:first-child,
  .imageBox br:nth-child(2),
  .erp-container,
  .columnRightPadding .module,
  #footer,
  iframe,
  .relatedVideoBox,
  .articleBody > div br:nth-child(2),
  img[src="http://static.howstuffworks.com//gif/videos/120x90/30746.jpg"],
  .share,
  .columnRight,
  #ArticleHeader .line .right,
  #BelowContentWell0,
  #YouMightAlsoLike1,
  #ABShare,
  #Share,
  #Share1,
  #Share2,
  .PageTip,
  #ABPagination,
  .footer {
    display: none !important;
  }

  .header {
    padding: 0 !important;
  }

  .channelNavigation ul {
    padding-left: 3px !important;
    border-bottom: 0 !important;
  }
  .channelNavigation a.catActive {
    background: #fff !important;
  }
  .channelNavigation a.navLink {
    padding: 0 18px !important;
  }

  #subChannelNav {
    height: auto !important;
    padding-left: 13px !important;
    background: #fff !important;
    border-left: 0 !important;
    border-right: 0 !important;
    overflow: hidden !important;
  }

  #subChannelNav li a {
    height: auto !important;
    padding: 8px 7px 7px !important;
  }

  .breadcrumb {
    margin: 12px 24px 0 !important;
  }

  .top10Head {
    float: none !important;
    width: auto !important;
    padding: 15px 20px 13px !important;
  }
  .top10SubHead2 {
    padding: 0 !important;
  }
  .top10body {
    margin: 0 !important;
  }

  .page,
  .layout1 .main {
    width: 960px !important;
  }
  .article {
    width: 960px !important;
  }
  img.article {
    width: 100% !important;
  }

  .articleByLine {
    padding: 0 !important;
  }

  #BreadCrumb,
  .header,
  .main {
    margin: 0 !important;
  }

  .article {
    border: 0 !important;
  }

  .article .content {
    height: auto !important;
    padding: 7px 15px !important;
  }

  .articleBody,
  .articleBody *,
  .snap_preview {
    font-size: 17px !important;
    line-height: 25px !important;
  }
  .articleBody p {
    margin-bottom: 25px !important;
  }
  .articleBody .imageBoxRight,
  .articleBody > div {
    margin: 0 !important;
  }
  .sideBoxLeft {
    margin: 6px 20px 20px 0 !important;
  }
  .sideBoxRight {
    margin: 5px 0 20px 20px !important;
  }

  .columnRight {
    background: transparent !important;
  }


  .stabs {
    overflow: visible !important;
  }
  #featuredItems .stabs ul,
  #featuredItems .stabs ul li {
    height: auto !important;
    margin: 0 !important;
  }
  #featuredItems .stabs ul li {
    width: 159px !important;
  }
  #featuredItems .stabs ul li a {
    display: block !important;
    height: 77px !important;
    padding: 0 !important;
    text-align: center !important;
  }
  #featuredItems .stabs ul li a:hover {
    background: #f0f6f9;
  }
  #featuredItems .stabs ul li .thumbnail {
    float: none !important;
    margin: 10px 0 3px !important;
  }
  #featuredItems .stabs ul li a span {
    display: block !important;
  }
  #featured-articles .on {
    display: block !important;
    position: relative !important;
    color: #fff !important;
    text-decoration: none !important;
    background: #006699 !important;
    overflow: visible !important;
    z-index: 2 !important;
  }
  #featured-articles .on:after {
    content: '' !important;
    display: block !important;
    position: absolute !important;
    top: -9px !important;
    left: 50% !important;
    margin-left: -9px !important;
    width: 0 !important;
    height: 0 !important;
    border: 9px solid #006699 !important;
    border-left-color: transparent !important;
    border-right-color: transparent !important;
    border-top: 0 !important;
    z-index: 3 !important;
  }

  .article .pagination {
    margin: 10px 15px !important;
  }

  #Pagination.module {
    margin: 0 !important;
  }

  .pageTipBox {
    clear: both !important;
  }

}
