css:
@-moz-document domain("www.roblox.com") {
/* ROBLOX STYLE CODENAME aeroblox | (C) 2021 John R. */
/* Roblox Acrylic Alpha by John R. */

.light-theme .rbx-header {
    background-color: #dee1e396;
    border: none;
    backdrop-filter: blur(6px);
    background-image: url(https://jkrosado.github.io/aeroacrylic.png)
}
.dark-theme .theme-bg {
    background-color: #2325274d;
    backdrop-filter: blur(12px);
}
#RollerContainer.rollercoaster-background, #InnerRollerContainer {
    background-attachment: fixed;
}
div#action-bar-container {
    backdrop-filter: blur(12px);
    position: fixed;
    height: 4em;
}

.light-theme .rbx-left-col {
    background-color: #ffffffbf;
    backdrop-filter: blur(6px);
    border: none;
}
.light-theme .popover {
    box-shadow: none;
}
.light-theme .dropdown-menu {
    background-color: #ffffff85;
    color: #393b3d;
    backdrop-filter: blur(12px)!important;
}
.light-theme .popover {
    background-color: #ffffff85;
    backdrop-filter: blur(12px)!important;
}
.ng-scope.card-with-game {
    background-color: #ffffff85;
    border-radius: 1em;
}
ul#settings-popover-menu {
    background: #ffffff00;
}
.chat-main, .light-theme .dialog-container, .light-theme .chat-main {
    background-color: #ffffff75;
    backdrop-filter: blur(6px)!important;
    background-image: url(https://jkrosado.github.io/aeroacrylic.png)
}
.light-theme .chat-container .chat-main .chat-header, .chat-windows-header.dialog-header {
    background-color: #dee1e300!important;
    backdrop-filter: blur(6px)!important;
}
.light-theme .dialogs .dialog-container .dialog-body {
    background-color: rgb(242, 244, 245, 33%);
}

.light-theme .rbx-left-col {
    transition: 0.5s;
}
li.navbar-search-option.rbx-clickable-li {
    background: #fff;
}
div#chat-main {
    transition: 0.4s;
}
.light-theme .dialog-container {
    transition: 0.4s;
}
.game-card-container {
    transition: 0.4s;
}
.game-card-container:hover {
    box-shadow: 0 1px 4px 0 rgb(25, 25, 25, 30%);
    padding: 5px;
    background: #ffffff9e;
    width: 160px;
    backdrop-filter: blur(6px)!important;
    transform: scale(1.05);
}
.people-list .friend {
    transition: 0.4s;
}
.people-list .friend:hover {
    box-shadow: 0 1px 4px 0 rgb(25, 25, 25, 30%);
    background: #ffffff9e;
    backdrop-filter: blur(6px)!important;
    transform: scale(1.05);
}
.people-list .friend:active {
    transform: scale(0.95)
}
.light-theme .rbx-tabs-horizontal .rbx-tab .rbx-tab-heading {
    transition: 0.4s;
}
.light-theme .rbx-header .rbx-navbar li {
    border: none!important;
}
a.font-header-2.nav-menu-title.text-header:hover {
    transform: scale(1.1);
    background: #00000017!important;
    border-radius: 2em;
}
a.font-header-2.nav-menu-title.text-header {
    transition: 0.4s;
}
a.font-header-2.nav-menu-title.text-header {
    border-radius: 2em;
}
.light-theme a.btn-common-play-game-lg, button#rbx-place-purchase-required {
    border-radius: 4px;
    transition: 0.4s;
}
a.btn-common-play-game-lg:hover, button#rbx-place-purchase-required:hover {
    transform: scale(1.05);
}
a.btn-common-play-game-lg:active, button#rbx-place-purchase-required:active {
    transform: scale(0.95);
    background-color: #008453;
    border-color: #008453;
    transition: 0.4s;
}
.game-card-container:active, a.font-header-2.nav-menu-title.text-header:active {
    transform: scale(0.995);
}
a.btn-growth-md.buy-robux {
    transition: 0.4s;
}
a.btn-growth-md.buy-robux:hover {
    transform: scale(1.05);
}
a.btn-growth-md.buy-robux:active {
    transform: scale(0.945);
    background-color: #008453;
    border-color: #008453;
}
.alert.alert-warning, .alert.alert-success {
    display: none;
}
li.list-item.item-card.ng-scope {
    transition: 0.4s;
}
li.list-item.item-card.ng-scope:hover {
    box-shadow: 0 1px 4px 0 rgb(25, 25, 25, 30%);
    background: #ffffff9e;
    backdrop-filter: blur(6px)!important;
    transform: scale(1.05);
}
.container-main .catalog-full-screen .catalog-results .item-cards-stackable .item-card, .container-main .splash.catalog-full-screen .catalog-results .item-cards-stackable .item-card {
    width: 15%;
}
li.list-item.item-card.ng-scope:active {
    transform: scale(0.95);
}
button.btn-fixed-width-lg.btn-growth-lg.PurchaseButton {
    transition: 0.4s;
}
button.btn-fixed-width-lg.btn-growth-lg.PurchaseButton:hover {
    transform: scale(1.05);
}
button.btn-fixed-width-lg.btn-growth-lg.PurchaseButton:active {
    transform: scale(0.945);
    background-color: #008453;
    border-color: #008453;
}
a#confirm-btn, a#decline-btn {
    transition: 0.4s;
}
a#confirm-btn:hover, a#decline-btn:hover {
    transform: scale(1.05);
}
a#confirm-btn:active {
    transform: scale(0.945);
    background-color: black;
}
a#decline-btn:active {
    transform: scale(0.945);
}
.light-theme .modal-dialog .modal-content {
    background-color: #ffffffb0;
    color: #393b3d;
    backdrop-filter: blur(5px);
}
div#simplemodal-overlay {
    backdrop-filter: blur(6px);
    background-color: transparent!important;
    transition: 0.4s;
}
/* this part is where the all normal button shrink and enlarge stuff goes */
button#unfriend-btn, button.btn-control-md.ng-binding, button.profile-join-game.btn-primary-md.ng-binding, span.btn-secondary-md.btn-more.rbx-private-server-create-button, button.PurchaseButton.btn-buy-md.btn-full-width.rbx-gear-passes-purchase, button#login-button, a.btn-full-width.btn-control-xs.rbx-game-server-join, button.btn-secondary-md.group-form-button.ng-binding, li.pager-next, li.pager-prev, button.input-dropdown-btn, button#group-join-button, a#inventory-button, button.ignore-friend.btn-secondary-md.btn-min-width, button.accept-friend.btn-cta-md.btn-min-width {
    transition: 0.4s;
}
button#unfriend-btn:hover, button.btn-control-md.ng-binding:hover, button.profile-join-game.btn-primary-md.ng-binding:hover, span.btn-secondary-md.btn-more.rbx-private-server-create-button:hover, button.PurchaseButton.btn-buy-md.btn-full-width.rbx-gear-passes-purchase:hover, button#login-button:hover, a.btn-full-width.btn-control-xs.rbx-game-server-join:hover, button.btn-secondary-md.group-form-button.ng-binding:hover, li.pager-next:hover, li.pager-prev:hover, button.input-dropdown-btn:hover, button#group-join-button:hover, a#inventory-button:hover, button.ignore-friend.btn-secondary-md.btn-min-width:hover, button.accept-friend.btn-cta-md.btn-min-width:hover {
    transform: scale(1.05)
}
button#unfriend-btn:active, button.btn-control-md.ng-binding:active, button.profile-join-game.btn-primary-md.ng-binding:active, span.btn-secondary-md.btn-more.rbx-private-server-create-button:active, button.PurchaseButton.btn-buy-md.btn-full-width.rbx-gear-passes-purchase:active, button#login-button:active, a.btn-full-width.btn-control-xs.rbx-game-server-join:active, button.btn-secondary-md.group-form-button.ng-binding:active, li.pager-next:active, li.pager-prev:active, button.input-dropdown-btn:active, button#group-join-button:active, a#inventory-button:active, button.ignore-friend.btn-secondary-md.btn-min-width:active, button.accept-friend.btn-cta-md.btn-min-width:active {
    transform: scale(0.945);
}
/* rest of css script below */
li.list-item.real-game-pass {
    transition: 0.4s;
}
li.list-item.real-game-pass:hover {
    box-shadow: 0 1px 4px 0 rgb(25, 25, 25, 30%);
    background: #ffffff9e;
    backdrop-filter: blur(6px)!important;
    transform: scale(1.05);
}
li.list-item.real-game-passe:active {
    transform: scale(0.95);
}
.store-card {
    margin: 0 5% 0 5%;
}
.light-theme .pager .first .pager-btn, .light-theme .pager .first a, .light-theme .pager .last .pager-btn, .light-theme .pager .last a, .light-theme .pager .pager-next .pager-btn, .light-theme .pager .pager-next a, .light-theme .pager .pager-prev .pager-btn, .light-theme .pager .pager-prev a {
    background-color: transparent!important;
}
.avatar-card-container:hover {
    transform: scale(1.05);
    box-shadow: 0 1px 4px 0 rgb(25, 25, 25, 30%);
}
.avatar-card-container {
    transition: 0.4s;
}
.avatar-card-container:active {
    transform: scale(0.95);
}
}
