css:
@charset "utf-8";
/* CSS Document */

@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("neogaf.com") {
body a:link {

		color:#000000 !important;

	}



	body a:visited {

		color:#4682B4 !important;

	}	



	body a:hover {

		color:#ff6600 !important;

	}



    body .page {
        width:910px !important; 
/* for forum to fill the width of page delete this line */
    }
body {

		background:url("http://i32.tinypic.com/e9fdlc.jpg") #000000 top center !important;

		background-attachment:fixed !important;}
/* BODY STUFF */
/* by hateradio (aka HofR) */

body, .vbmenu_control {
color: #333 !important;
font-family: Arial !important;
}

table[id^="post"] .smallfont {
font-family: Arial !important;
}

.vbmenu_control {
background: #4682B4 !important;
}

div > div > div > table > tbody > tr > td {
-moz-border-radius: 3px !important;
}

/* nav stuff */
div > div > div > table > tbody > tr > td.alt1 > *,
div > div > div > table > tbody > tr > td.alt1 > table > tbody > tr > * {
font-family: Arial !important;
}

/* welcome box */
div > .tborder > tbody > tr > td[nowrap="nowrap"] strong {
font-family: Arial !important;
font-size: 12px
}

.vbmenu_control {
-moz-border-radius: 0px !important;
}

.vbmenu_control:first-child {
-moz-border-radius-topleft: 5px !important;
-moz-border-radius-bottomleft: 5px !important;
}

.vbmenu_control:last-child {
-moz-border-radius-topright: 5px !important;
-moz-border-radius-bottomright: 5px !important;
}

/* nav */
font {
color: #4F4E4A !important
}

.tcat {
background: #4682B4 !important;
color: #3a3a3a !important;
-moz-border-radius-topright: 0px !important;
-moz-border-radius-bottomright: 0px !important;
}

table[style*="3px"] {
margin: 0 !important;
}

.thead {
background: #4682B4 !important;
-moz-border-radius: 2px;
color: #444 !important;
}
.page {
background: none !important
}


.panel {
border: 0px !important;
background: #E1E1DB !important;
}

.tborder, .panelsurround, .tfoot {
border: 1px solid #f1f1f1 !important;
background: #f1f1f1 !important;
padding: 4px;
}

.pagenav .tborder {
background: #f1f1f1 !important;
}

.vbmenu_popup {
border: 0  !important;
}

.pagenav {
display:block;
float: right;
}

.pagenav > .tborder {
display: block;
-moz-border-radius: 3px !important;
height: 27px;
margin: 3px 0;
padding: 3px;
}

#inlinemodform > table > tbody > tr > td[align="right"]:last-child,
.page > div > table > tbody > tr > td[align="right"]:last-child {
background: #f1f1f1;
color: #f1f1f1;
padding-top: 1px;
text-align: center !important;
display: block;
margin: 3px 0;
height: 25px;
width: 148px !important;
float: right;
-moz-border-radius: 3px;
}

#inlinemodform > table > tbody > tr > td[align="right"]:last-child a,
.page > div > table > tbody > tr > td[align="right"]:last-child a {
display: inline-block !important;
background: #f1f1f1;
padding-top: 6px
}

div > div[style*="6px"] {
padding: 0 !important;
}

.time {
color: #555 !important;
}


/* THREADS */

#threadslist > tbody > tr > td.alt2 {
-moz-border-radius: 0px;
-moz-border-radius-topright: 3px;
-moz-border-radius-bottomright: 3px
}

/* thread images */

/* new */

/* front pg */

img[src$="forum_old_lock.gif"],
tbody[id^="collapseobj_"]:not(.inlineimg) img {
opacity: 0 !important
}

/* all bg */


a[href*="showthread.php?goto=newpost&t="] img {
opacity: 0 !important
}

#threadslist img {
opacity: .75
} 

#threadslist > tbody > tr > td[id^="td_status"] {
background-color: #D1D1C9 !important;
background-image: url(http://i39.tinypic.com/1zveh69.png) !important;
background-position: center !important;
background-repeat:no-repeat !important;
display: inline-block !important;
width: 32px;
height: 42px !important;
}

.alt1 a[href*="showthread.php?goto=newpost&t="] {
background-image: url(http://i44.tinypic.com/14se2x3.png) !important;
background-color: #D1D1C9 !important;
background-repeat: no-repeat !important;
display:block;
width: 32px !important;
height: 42px !important;
background-position: center !important;
}

#threadslist > tbody > tr > td[id^="td_title_"].alt1 {
-moz-border-radius: 0px;
}

#threadslist > tbody > tr > td[id^="td_title_"].alt1:hover {
-moz-border-radius: 0px;
background: #D1D1C9 !important;
}

#threadslist > tbody > tr > td[align="center"].alt2 {
font-size: 11px !important;
}

#threadslist > tbody > tr > td[align="center"].alt1 {
-moz-border-radius: 3px;
font-size: 11px !important;
}

.alt1, .alt1Active, .alt2, .alt2Active {
font-family: Arial !important;
font-size: 12px !important;
border: 0 !important;
color: #222 !important;
}

.alt1 .smallfont, .alt1Active .smallfont, .alt2 .smallfont, .alt2Active .smallfont {
display: block;
}

/*table[id^="post"]*/

.alt1, .alt1Active, .alt2, .alt2Active {
-moz-border-radius-topright: 0px;
-moz-border-radius-bottomright: 0px;
-moz-border-radius-topleft: 5px;
-moz-border-radius-bottomleft: 5px;
}

table[id^="post"] > tbody > tr > .alt1,
table[id^="post"] > tbody > tr > .alt1Active,
table[id^="post"] > tbody > tr > .alt2,
table[id^="post"] > tbody > tr > .alt2Active {
-moz-border-radius-topright: 0px !important;
-moz-border-radius-bottomright: 0px !important;

}

table[id^="post"] > tbody > tr > td[id^="td_post_"] {
-moz-border-radius-topright: 5px  !important;
-moz-border-radius-bottomright: 5px  !important;
-moz-border-radius-topleft: 0px  !important;
-moz-border-radius-bottomleft: 0px  !important;
padding-left: 0px !important;
}
/* SIDES */

table[id^="post"] > tbody > tr > td[width="135"] {
width: 100px !important;
overflow: hidden;
padding-right: 0 !important;
}
table[id^="post"] > tbody > tr > td[width="135"] .smallfont {
/* overflow: hidden;
 width: 120px !important; */
}

td[width="135"] br {
display: none !important
}

span[id^="postmenu_"] + .smallfont,
span[id^="postmenu_"] + .smallfont + .smallfont {
font-size: 11px !important;
font-family: Arial !important;
}

span[id^="postmenu_"] + .smallfont + .smallfont {
height: 13px;
}

/* date */

span + div.smallfont + div.smallfont {
width: 100px !important;
font-size: 9px !important;
margin-bottom: 3px;
}

/* usertitle  */

span + div.smallfont {
/*background: #CFCFC2;
padding: 2px 3px;
-moz-border-radius: 2px;
margin: 3px 0;
border: 1px solid #AFAFA4 !important;*/
font-weight: bold !important;
color: #545454;
width: 90px;
}

span + div.smallfont > a:link,
span + div.smallfont > a:visited {
text-decoration: underline !important;
}

img[src*="reputation/reputation"] {
display: none !important;
}

/* MORE */

.alt1, .alt1Active {
background: #E1E1DB !important;
}

.alt2, .alt2Active {
background: #d1d1c9  !important
}

/* CODE */

pre.alt1,
pre.alt2,
.quotearea pre
 {
	background-color: #444 !important;
	color: #eee !important;
	width: auto !important;
	min-height: 90px !important;
	/*background-image: url(http://speepo.com/images/html.png) !important;
	background-repeat: no-repeat !important;
	border: 1px solid #cCC !important;
	background-position: 98% 20px !important; */
	-moz-border-radius: 5px;
	padding-bottom: 0 !important;
	font-family:"Courier New", Courier, monospace !important;
	max-width: 800px;
}

.alt {
background: #E1E1DB !important;
}

/* avatar */
td[width="135"] img[alt$="Avatar"] {
display: block;
/* margin-left: 4px; 
border-bottom: 3px solid #666 !important;
border-top: 1px solid #666 !important*/
}

.inlineimg {
vertical-align: middle !important
}

/* POSTS */

.quotearea a:link,
.quotearea a:visited,
div[id^="post_message_"] a:link,
div[id^="post_message_"] a:visited {
border-bottom: 1px dotted #888
}

.quotearea a:hover,
.quotearea a:visited:hover,
div[id^="post_message_"] a:link,
div[id^="post_message_"] a:visited {
border-bottom: 1px dotted #74818F
}

div[id^="post_message_"] {
font-size: 13px;
color: #333
}

/* SPOILER +ROLL */

.spoiler {
margin-top: 4px;
/*display: block;
-moz-border-radius: 5px !important;
padding: 4px;*/
cursor: pointer;

background: #2A2A2A !important;
color: #333 !important;
}

.spoiler:hover {
/*display: block;
-moz-border-radius: 5px !important;*/
color: #EEE !important;
}

/* LINKS */


a:link, a:visited {
color: #4F4E4A  !important
}
a:hover, a:visited:hover {
color: #1B426F !important;
}

.bigusername {
font-size: 15px;
vertical-align: middle !important;
display: block;
border-bottom: 1px dotted #AFAFA4;
margin-bottom: 2px;
font-family: Arial !important;
}

/* MISC */

hr {
/* border-top: 1px dotted #AFAFA4 !important; */
border-top: 1px dotted #AFAFA4 !important;
margin-top: 18px !important;
width: 100% !important
}

div.smallfont > span  > strong{
display: none !important
}

.quotearea {
border: 1px solid #AAA !important;
background: #dfded9 !important;
-moz-border-radius: 5px;

}

.quotearea div {
font-style: normal !important;
font-family: Arial;
font-size: 12px
}

/* REPLY/NEW THREAD IMGs */

/* img[src$="reply.gif"], img[src$="newthread.gif"] {
border: 3px solid #f1f1f1 !important;
height: 30px !important;
}*/

td.smallfont a[href*="newreply.php"] img, td.smallfont a[href*="newthread.php"] img {
opacity: 0
}

td.smallfont a[href*="newreply.php"] {
display:block;
background: #f1f1f1;
height: 27px;
margin: 3px 0;
width: 100px;
overflow: hidden !important;
text-align: center;
background-image: url(http://i43.tinypic.com/xeflw.png);
background-repeat: no-repeat;
background-position: center 0px; 
-moz-border-radius: 3px
}

td.smallfont a[href*="newreply.php"]:hover {
background: #eee;
background-image: url(http://i43.tinypic.com/xeflw.png);
background-repeat: no-repeat;
background-position: center -26px; 
-moz-border-radius: 3px
}

td.smallfont a[href*="newthread.php"] {
display:block;
background: #f1f1f1;
height: 27px;
margin: 3px 0;
width: 100px;
overflow: hidden !important;
text-align: center;
background-image: url(http://i43.tinypic.com/f3ho3r.png);
/*background-image: url(http://i43.tinypic.com/o5q0bs.png);
 background-image: url(http://xs134.xs.to/xs134/08516/ce214.png); */
background-repeat: no-repeat;
background-position:center 1px; 
-moz-border-radius: 3px
}

td.smallfont a[href*="newthread.php"]:hover {
background: #eee;
background-image: url(http://i43.tinypic.com/f3ho3r.png);
background-repeat: no-repeat;
background-position:center -27px;
-moz-border-radius: 3px
}

/* REPLY / QUOTE / EDIT/DEL */

table[id^="post"] > tbody > tr > td > .smallfont > a[href^="newreply.php?"],
#post > tbody > tr > *  a[href^="private.php?do=newpm&pmid"] {
background: #F18219;
display: inline-block;
color: #FFF !important;
padding: 1px;
text-align: center;
font-size: 11px !important;
/* -moz-border-radius: 2px; */
width: 43px !important;
-moz-border-radius-topright: 2px;
-moz-border-radius-bottomright: 2px;
margin-bottom: 3px
}

a[href$="noquote=1"] {
background: #cf6504 !important;
-moz-border-radius-topright: 0px !important;
-moz-border-radius-bottomright: 0px !important;
-moz-border-radius-topleft: 2px;
-moz-border-radius-bottomleft: 2px
}

a[href$="noquote=1"]:hover {
background: #555 !important
}

table[id^="post"] > tbody > tr > td > .smallfont > a[href^="newreply.php?"]:hover,
#post > tbody > tr > * a[href^="private.php?"]:hover {
background: #444;
}

a[href^="private.php?do=newpm&forward=1"],
table[id^="post"] > tbody > tr > td > .smallfont > a[href^="editpost.php?"] {
background: #BFBFB3;
padding: 1px;
width: 88px !important;
display: block;
/* -moz-border-radius: 3px; */
font-size: 11px !important;
text-align: center;
}
a[href^="private.php?do=newpm&forward=1"]:hover,
table[id^="post"] > tbody > tr > td > .smallfont > a[href^="editpost.php?"]:hover {
background: #444;
color: #CCC !important
}

div.smallfont + div.smallfont + div.smallfont  {
color: transparent !important;
font-size: 0px !important;
margin-top: 3px
}

div.smallfont + div.smallfont + div.smallfont:last-child  {
margin-top: 0px
}

.tdfoot:after {
content: "by hateradio" !important;
display: block !important;
text-align: center;
white-space: pre;
}

/* pm border */
.page > div >table > tbody > tr > td > form > #post > tbody > tr > td[id^="td_post"] {
border: 0 !important;
}

/* POP MENUs */
.vbmenu_option {
background: #666 !important;
}

.vbmenu_option > a,
.vbmenu_hilite a {
color: #efefef !important;
font-family: Arial, Helvetica, sans-serif;
}

.vbmenu_hilite {
background: #444 !important;
}

td[id^="td_status_"] img[src$="statusicon/thread.gif"] {
opacity: 0 !important
}

a[href^="showthread.php?goto=lastpost"] img {
opacity: 0 !important
}
a[href^="showthread.php?goto=lastpost"] {
background: url(http://i44.tinypic.com/6r59w4.gif);
}


.tfoot > .smallfont {
color: #555
}

/* MOZ CLOSE */
}
