/* Vous pouvez changer les couleurs ici */
:root {
--console-color: #f8c428; /* Couleur claire */
	--console-bg: #221205; /* Couleur sombre */
	--text-size: 18px; /* Taille du texte */
	--loading: .5s; /* Durée du balayage */
}

* {
	background: var(--console-bg) !important;
	border-radius: 0 !important;
}

.glyphicon {
	color: var(--console-color) !important;
	background: transparent !important;
}

.topic-icon a {
	border: none !important;
}

tr.active, .table>tbody>tr.active>td, .table>tbody>tr.active>td * {
	color: var(--console-bg) !important;
	background: var(--console-color) !important;
}

.form-control, .preview, .btn {
	color: var(--control-color) !important;
}

.form-control:focus{
	box-shadow: inset 0 1px 1px rgb(0 0 0 / 8%), 0 0 8px var(--console-color);
}

.topic-icon img {
	filter: grayscale(1) brightness(100);
}

img[alt="Topic topic rouge"]{
	animation: blink-animation 2s steps(5, start) infinite;
  -webkit-animation: blink-animation 2s steps(5, start) infinite;
}

@keyframes blink-animation {
  to {
    visibility: hidden;
  }
}

body {
  background-image: url(data:image/png;base64,) !important;
  }

