/* Custom scrollbar */
::-webkit-scrollbar {
    background-color: var(--console-bg);
    color: var(--console-color);
}
::-webkit-scrollbar-corner {
    background-color: var(--console-color);
}
::-webkit-scrollbar-thumb {
    background-color: var(--console-color);
}

body::after {
  content: " ";
  display: block;
  position: absolute;
  top: 0;
  left: 0;
  bottom: 0;
  right: 0;
  background: rgba(18, 16, 16, 0.1);
  opacity: 0;
  z-index: 2;
  pointer-events: none;
  animation: flicker 0.15s infinite;
}

body::before {
  content: " ";
  display: block;
  position: absolute;
  top: 0;
  left: 0;
  bottom: 0;
  right: 0;
  background: linear-gradient(rgba(18, 16, 16, 0) 50%, rgba(0, 0, 0, 0.25) 50%), linear-gradient(90deg, rgba(255, 0, 0, 0.06), rgba(0, 255, 0, 0.02), rgba(0, 0, 255, 0.06));
  z-index: 2;
  background-size: 100% 2px, 3px 100%;
  pointer-events: none;
}

/* logo */
.navbar .navbar-logo img {
	display: none;
}

.form-control, textarea {
	color: var(--console-color);
}

.message-content-quote {
	background: rgba(0,0,0, 0.5) !important;
	color: var(--console-color);
}

.navbar-toggle .icon-bar {
  border: 1px solid var(--console-color);
}

.navbar .navbar-logo:before{
	content: "Terminoel";
	margin-top: -20px;
	display: block;
	font-size: 2em
}

html {
	perspective: 200px;
	
}


body {
  /*animation: textShadow 1.6s infinite;*/
	color: var(--console-color);
	background: var(--console-bg) !important;
  	-webkit-font-smoothing: none;
  	line-height: 1.2;
	padding-top: 80px;
	
}

* {
  box-sizing: border-box;
}

.btn:hover, .btn:hover .glyphicon, .btn:hover img, .navbar-user li:hover .glyphicon, .navbar-links li:hover .active, .navbar-links li.active:hover a, .pull-left:hover .glyphicon, .pull-right:hover .glyphicon {
	background: var(--console-color) !important;
	color: var(--console-bg) !important;
}

img {
  image-rendering: -moz-crisp-edges;
  image-rendering: -webkit-crisp-edges;
  image-rendering: pixelated;
  image-rendering: crisp-edges;
}

body, * {
  color: var(--console-color);
  font-family: 'VT323';
  -webkit-font-smoothing: none;
	
}

.fancybox-container * {
	background: transparent !important;
}

.fancybox-container {
	background: #00000090 !important;
}

/*.fancybox-content {
	display: contents;
}*/

.fancybox-active {
  height: 100%;
}

.fancybox-content {
	transform: unset !important;
}

#app {
	height: 100%;
	overflow-y: scroll;
	padding-bottom: 100px !important;
}

#app  *{
	font-size: var(--text-size) !important;
}

a,
a:visited {
  border-bottom: 0.2rem solid var(--console-color);
  color: var(--console-color) !important;
  text-decoration: none;
	font-family: "VT323" !important;
}

a:active,
a:focus,
a:hover {
  background-color: var(--console-color) !important;
  color: var(--console-bg) !important;
}


/* screen bevel */

body {
  background-color: transparent;
  border-radius: 2rem;
  outline: 2rem solid black;
  box-shadow:
    inset 0 0 18rem black,
    inset 0 0 3rem black;
  height: 100%;
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;

}

.navbar, .footer, * {
	border-color: var(--console-color) !important;
}

body {

  background-size: 100% 0.3rem;
  border-radius: 2rem;
	speak: none;
	font-style: normal;
	font-weight: normal;
	font-variant: normal;
	text-transform: none;
	line-height: 1;
	-webkit-font-smoothing: antialiased;
	-moz-osx-font-smoothing: grayscale;
	transform: matrix3d(
		1,0,0,0,
		0,1,.01,0,
		0,0,1,0,
		0,-15,0,1);
}

/* screen glow*/
