@keyframes crt-glow {
  0% { opacity: 0.1; }
  50% { opacity: 0.2; }
}

/* make text selection look good*/

::-moz-selection {
  background-color: var(--console-color)r;
  color: var(--console-bg);
}
::selection {
  background-color: var(--console-color);
  color: var(--console-bg);
}

img, .table>tbody>tr.active>td img {
	background: transparent !important;
}
}
