css:
@-moz-document domain(typing.com) {

.screen, .structure {
	    box-sizing: border-box;
	    font-family: Helvetica, sans-serif;
	    margin-bottom: 20px;
	    position: relative;
	}

.structure-advert, .advert {
		display: none !important;
	}

.wrapper {
    border-right: none;
    border-left: none;
    box-shadow: none;
}

 .hero, .hero--lesson {
  background-color: #0b85bb !important;
    background-image: none;
  margin: 0 -9999rem;
  padding: 0.25rem 9999rem;
  margin-bottom: 115px;
  height: 64px;
  position: static;
}

html, body {
  overflow-x: hidden;
}

.lessonNav-lesson, .list--flag {
    font-size: 15px;
    margin-top: 18px;
}

.bucket-content {
    position: relative;
    bottom: 14px;
}

}
