@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document 
    domain("hardware.no"),
    domain("hwb.no"),
    domain("akam.no"),
    domain("amotor.no"),
    domain("kulturo.no"),
    domain("diskusjon.no") {

  #page {
    margin-top: 10px !important;
    margin-bottom: 20px !important;
  }

  #ad_140x350_2,
  #ad_200,
  #ad_160, 
  #ad280,
  #ad468x400,
  #ad768,
  #ad996_1a, 
  #ad996_bottom,
  #ad996bottom,
  #ad996x150,
  #adSidebar,
  #adText,
  #span160,
  #textAds,
  .ad,
  .ad.begin,
  .ad180x100,
  .ad180x690,
  .ad180x800,
  .ad200x150,
  .ad200x300,
  .ad468x60,
  .ad468x400,
  .ad768x150,
  .ad_200,
  .adBottom,
  .sponsoredProductWrapper,
  iframe {
    width:0px !important;
    height:0px !important;
    display:none !important;
  }
}

@-moz-document domain("gamer.no") {
  #ad160_1,
  #ad160_2,
  #ad160_side,
  #ad200_1a, 
  #ad468_1a,
  #ad468_2a,
  #ad996_1a,
  #adText_1a,
  .ad200,
  .ad468
  {
    display:none !important;
  }

  .dropdown { top:113px !important; }

  #sectionImage { top:2px !important; }

}

@-moz-document 
    domain("amobil.no"),
    domain("diskusjon.no") {

  #ad180_sidebar,
  #bunnbanner,
  #topbanner,
  #topbanner2,
  #topTextAds,
  .adbox {
    display:none !important;
  }

  #right div.search {top:5px !important;}
}
