/* ==UserStyle==
@name           Multi-Column Lightwood
@namespace      USO Archive
@author         MrQuinn
@description    `Provides 2 column stream to MyWorld and Groups`
@version        2021.4.12.v1
@license        CC0-1.0
@preprocessor   uso
==/UserStyle== */
@-moz-document url("https://mewe.com/myworld"),
url("https://mewe.com/groups"),
url("https://mewe.com/group") {

	body,
	header[class="main-header_inner h-flex_center_x_y"],
	header[class="mw_box_header group-bg group-bg-secondary"],
	header[class="mw-box_header group-bg group-bg-secondary"],
	div[class="mw-box c-mw-sidebar-photostream ember-view"] {
		background: url("http://res.cloudinary.com/mrquinn/image/upload/v1465660031/styles/lt_wood_bkg.jpg");
		background-attachment: fixed;
		-webkit-background-size: cover;
		-moz-background-size: cover;
		-o-background-size: cover;
		background-size: cover;
		color: #f4c40cfa;
	}
	
	.content-all-wrapper.ember-view {
		max-width: 100%;
	}
	.c-feed-simple.ember-view {
		display: grid;
      /* ALTER 70% TO 33% THREE COLUMNS */
		grid-template-columns: 65% 65%;
		grid-row-gap: 0px;
		grid-column-gap: 5px;
		max-width: 100%;
	}
	[id="ember12"],
	[id="ember6454"],
	div[class="mw-box"],
	span[class="mw-box_header"],
	div[class="mw_box_header"],
	span[class="mw-box_title"],
	div[class="sidebar-topics_list-wrapper win-scrollbar"],
	div[class="mw-box_footer feature-hint_wrapper"],
	div[class="mw-box c-mw-sidebar-wrappers ember-view"],
	div[id="ember3030"],
	[class="text-editor notranslate c-text-editor postbox_header_textarea win-scrollbar ember-view"],
	[class="placeholer-postbox_wrapper"],
	div[class="w-100"],
	footer[id="ember2934"],
	[class="mw-box mw-box--row flex-wrap mw-box_links c-mw-links-footer ember-view"],
	footer[id="ember73"],
	[class="mw-box mw-box--row flex-wrap mw-box_links c-mw-links-footer ember-view"] {
		background: url("http://res.cloudinary.com/mrquinn/image/upload/v1465660031/styles/lt_wood_bkg.jpg");
		background-attachment: fixed;
		color: #f4c40cfa;
	}
}
