@-moz-document regexp("https://nyaa.si/") {
    body {
    font-family: Arial;
    font-size: 1.3em;
    font-weight: Bold;
    background-color: #FBFBFB;
}

/* remove bold from search */
input.form-control:nth-child(3) {
    font-weight: normal;
}

a {
    color: #106655;
}

a:hover {
    text-decoration: underline;
    color: #106655;
}

table.torrent-list tbody tr td a:visited {
    color: #106655;
}

/* trusted */
body .torrent-list > tbody > tr.success > td {
    color: inherit;
    background-color: #98D9A8;
}
body .torrent-list > tbody > tr.success:hover > td {
    background-color: #98D9A8;
}

/* remakes */
body .torrent-list > tbody > tr.danger > td {
    color: inherit;
    background-color: #F0B080;
}
body .torrent-list > tbody > tr.danger:hover > td {
    background-color: #F0B080;
}

th.hdr-link.text-center {
    visibility: hidden;
}

th.hdr-link.text-center:after {
    visibility: visible;
    position: relative;
    top: 0;
    left: -12px;
    right: 0;
    bottom: 0;
    content: "DL";
}

/* old download icon */
.fa-download {
    background: url("https://i.imgur.com/8oEV2Tr.png");
    height: 16px;
    width: 16px;
}
.fa-download::before {
    content: "a";
    font-size: 0px;
}


/* new old-style magnet icon */
.fa-magnet {
    background: url("https://i.imgur.com/iuoDiBy.png");
    height: 16px;
    width: 16px;
}
.fa-magnet::before {
    content: "a";
    font-size: 0px;
}

/* torrent size */
.torrent-list > tbody > tr > td:nth-child(4) {
    font-weight: normal;
}

/* header */
#navbar {
    padding-left: 0px!important;
    margin-left: 0px!important;
}

a.navbar-brand {
    font-size: 0px;
}

a.navbar-brand:after {
    font-size: 13px;
    content: "Browse";
    font-weight: normal;
    color: #FBFBFB;
}

#navbar > ul.nav > li > a {
    font-weight: normal;
    color: #FBFBFB;
}

/* resize columns */
.hdr-size {
    width: 1px!important; /* will autoresize to match */
}

/* navbar */
.navbar {
    background: url("https://i.imgur.com/vtRerJr.png");
    height: 40px;
    min-height: 40px;
    max-height: 40px;
    padding-top: 0px;
    padding-bottom: 0px;

}
.navbar-inverse {
    color: white;
}

.navbar-nav > li > a {
    padding-top: 10px;
    padding-bottom: 0px;
}

.navbar-brand {
    padding: 10px 15px;
}

.navbar-form {
    margin-top: 3px;
}

.visible-lg {
    padding-top: 9px!important;
    padding-bottom: 0px!important;
}

/* search bar */
.navbar-form.navbar-right.form {
    margin-top: 7px;
}
.btn.dropdown-toggle {
    font-size: 10px!important;
    max-height: 25px!important;
}
.search-bar {
    font-size: 10px!important;
    max-height: 25px!important;
}
.search-btn > button {
    text-align: center;
    padding-top: 4px;
    font-size: 10px!important;
    max-height: 25px!important;
    height: 25px!important;
    background-color: #60B0F0;
    border-color: #60B0F0;
}

.btn-primary:hover{
    background-color: #609af0;
    border-color: #609af0;
}
.search-btn {
    max-height: 25px!important;
    height: 25px!important;
    
}

/* torrent table borders */
.table-responsive {
    border-top:1.3px solid rgba(68,68,68, 1);
    border-radius: 4px;
}

.table.table.table-bordered.table-hover.table-striped.torrent-list{
    border: none;

}

.table > thead > tr > th, .table > tbody > tr > th, .table > tfoot > tr > th, .table > thead > tr > td, .table > tbody > tr > td, .table > tfoot > tr > td{border: 1.3px solid rgba(68,68,68, 1)}

.pagination>.active>a{background-color: #106655;
border-color: #106655;}

