css:
/* i really want this to be global */
@-moz-document domain('docs.microsoft.com'){
body
{
    background: #333333;
    color: #dfdfdf;                                        
}
code
{
    background-color: #555 !important;
    color: #ffffff;
}
footer{
	background: #444 !important;
}
button{
	background: #555;
    color: #dfdfdf;
}
.button{
	background: #777;
}
.button.is-primary{
	color: var(--theme-text-invert)!important;
}
.nav-bar-button{
	background: #333 !important;
}
.checkbox-toggle-wrapper label.switch{
	background-color: var(--theme-body-background-dark);
}
.input, .textarea{
	background: var(--theme-body-background-dark);
    color: #fff!important;
}
.input::placeholder{
	color: #999;
}
.hljs-literal{
	color: #12ce8a;
}
.hljs-keyword{
	color: #b9b9ff;
}
.hljs 
{
	color:#adadff;
}
.hljs-title{
	color: #43dcff;
}
.hljs-comment{
	color: #01f901;
}
.hljs-string{
	color: #ff9c9c;
}
a:visited{
	color: #df9fff;
}
a{
    color: #43acfd;
}
.nav-bar-button{
    color: #ffffff;
}

.tree-item.is-leaf{
	color: #ffffff !important;
}
.codeHeader{
	background: #373737;
}
pre{
    background: #515151;
}

.hljs-built_in, .hljs-keyword, .hljs-name, .hljs-pscommand, .hljs-selector-tag, .hljs-tag {
    color: #4be6ff;
}
.hljs-attr {
    color: #77b8ff;
}
.parameterName, dl.parameterList dt{
	background-color: #5d6dff;
}
.theme-light, :root{
	--theme-text: #dfdfdf;
    --theme-text-invert: #222;
    --theme-secondary-hover: #222;
    --theme-hover-invert: #222;
    --theme-success-invert: #222;
    --theme-primary-base: #00ebf7;
    --theme-body-background-dark: #505050;
    --theme-border: #555;
    --theme-info-background: #856bb5;
    --theme-table-row-header: #212121;
    --theme-table-header: #848484;
    --theme-info-dark: #ffffff;
}
}
