@-moz-document domain("answers.unity.com") {
/*Unity Answers*/

body, html,

section.section-content
{
  background: #3d3d3f;
}

.tags .tag,

.answer-list .widget-content .answer-container, .answer-list .widget-content .reply-container,

.pagination ul > li > a, .pagination ul > li > span
{
    background: #363639;
}

pre,

.btn,

.pagination ul > li > a:hover, .pagination ul > li > a:focus, .pagination ul > .active > a, .pagination ul > .active > span
{
    background: #2e2e2f !important;
}

li.L1, li.L3, li.L5, li.L7, li.L9,

.navbar-inner .navbar-search input,
.row-fluid .span11 textarea,

#previewer,

code
{
    background: #12121354 !important;
}

nav.sub-nav,
header.section-header div.top-fill
{
  
    background-color: #222c37;
}

.accepted-answer .answer,
.question-action .counts p.answers.accepted, .question-list-item .counts p.answers.accepted, .node-list-item .counts p.answers.accepted
{
    background-color: #1d3911;
    color: #468847;
}

.form-actions,

ol.linenums code
{
  background: #0000 !important;
}

body, h1, h2, h3, h4, h5, h6,

.navbar-inner .navbar-search input,
.row-fluid .span11 textarea,

.btn
{
  color: #ccc !important;
}

.comment-text
{
  color: #999;
}

.kwd
{
  color: #3b73b9;
}

.com
{
  color: #b93b3b;
}

.typ
{
  color: #a43bb9;
}

.pln, pre
{
  color: #ccc;
}

*
{
  border: none !important;
  box-shadow: none !important;
}

.mb40
{
  margin-bottom: -10px !important;
}

header.section-header div.shard:before,
header.section-header div.logo a
{
  right: 0px;
  top: -25px !important;
  
  filter: drop-shadow(5px 5px 5px #00000070);
} 

.navbar-inner .navbar-search
{
  z-index: 100000;
}

clearImage 
{
    filter: invert(1) hue-rotate(180deg) saturate(2) brightness(.8);
    mix-blend-mode: color-dodge;
}
}

@-moz-document domain("forum.unity.com") {
/*Unity Forum*/

body, html,

#content .pageContent
{
  background: #3d3d3f;
}

.PanelScroller .scrollContainer, .PanelScrollerOff .panel,
.PanelScroller .panel, .PanelScrollerOff .panel,

.breadcrumb,

.messageList .message:nth-of-type(even),

.sectionFooter,

.nodeList .categoryStrip,

.attachment .boxModelFixer,

div.header-panel
{
    background: #363639;
}

.bbCodeBlock pre, .bbCodeBlock .code,
.bbCodeBlock .type,

.secondaryContent,

.Menu,
.navTabs .navTab .tabLinks .subMenu
{
    background: #2e2e2f !important;
}

.breadcrumb .crust a.crumb,

.bbCodeQuote .quoteContainer,

.textCtrl, .formPopup,

div.primaryContent.menuHeader,

.attachedFiles .attachedFilesHeader,
.attachedFiles .attachmentList,

div.user-panel a.link:hover
{
    background: #12121354 !important;
}

.bbCodeQuote .quoteContainer .quoteExpand
{
background: linear-gradient(to bottom, rgba(240, 240, 240, 0) 0%, #2e2e2f 80%);}

body, h1, h2, h3, h4, h5, h6,

.messageInfo .DateTime,
.bbCodeBlock pre, .bbCodeBlock .code, .bbCodeBlock .type,

.navTabs .navTab.selected .tabLinks a,

.textCtrl,

.titleBar h1 em,
.searchResult .snippet a,

.nodeList .categoryStrip *,
.node .unread .nodeText .nodeTitle a,

.navTabs .navTab.PopupClosed .navLink,
.blockLinksList a, .blockLinksList label,

.breadcrumb .crust a.crumb,
.linkGroup a,

.messageUserBlock a.username,

div.user-panel a.link
{
  color: #ccc !important;
}

.bbCodeBlock *[style*="#0600FF"]
{
  color: #3b73b9 !important;
}

.bbCodeBlock *[style*="#0000FF"]
{
  color: #3b54b9 !important;
}

.com
{
  color: #b93b3b;
}

.typ
{
  color: #a43bb9;
}

.primaryContent a,
.node .nodeDescription a
{
  color: #999 !important;
}

*
{
  border: none !important;
  box-shadow: none !important;
}

.breadcrumb .crust .arrow {
  background: #2a2a2c;
  width: 15px;
  height: 15px;
  top: 5px;
  right: -9px;
  
  border-right: 1px solid #cccccc75 !important;
  border-top: 1px solid #cccccc75 !important;
  transform: rotatez(45deg);
}

.main-menu, .legal
{
  display: inline-flex;
}

.main-menu li, .secondary-menu li, .legal li
{
  margin: 5px 10px !important;
}

div.main-navigation ul li a
{
  line-height: 20px;
  transform: translatey(30%)
}

.g8
{
  position: relative;
  transform: translateX(25%);
}

clearImage 
{
    filter: invert(1) hue-rotate(180deg) saturate(2) brightness(.8);
    mix-blend-mode: color-dodge;
}
}

@-moz-document domain("id.unity.com") {
body, html
{
  background: #3d3d3f;
}

div.block,

div.item-list .item
{
    background: #363639;
}

div.alert,

header.main-header,

div.block-header,

footer.main-footer div.wrapper,

div.sidebar-navigation ul li ul,

div.block-footer,

div.form-field-helper,

div.user-head,

header.main-header .slide-panel-tool .slide-panel
{
    background: #2e2e2f !important;
}

#accept-updated-tos .tos-box,

div.user-head .user-panel-item:hover,
header.main-header .nav-panel > a:hover
{
    background: #12121354 !important;
}

body, h1, h2, h3,

header.main-header a.app-name, header.main-header a.app-name:hover,

div.item-list .item div.lbl,
.ico-edit:before,

div.sidebar-navigation ul li a,

div.user-head .profile-name,
div.user-head .profile-email,
div.user-head .user-panel-item,
header.main-header .nav-panel > a
{
  color: #ccc !important;
}

*
{
  border: none !important;
  box-shadow: none !important;
}

div.sidebar-navigation ul li ul:before,

div.form-field-helper:before
{
  border-bottom-color: #2e2e2f;
}

div.form-field-helper,

div.user-head,
header.main-header .slide-panel-tool .slide-panel
{
  box-shadow: 0 5px 5px 2px #0005 !important;
}

header.main-header a.unity-logo,

header.main-header div.main-tools a.tool div.icon,
header.main-header a.user-icon > i
{
    filter: invert(1) brightness(1);
}
}

