css:
@-moz-document domain("app.slack.com") {
.p-channel_sidebar .p-channel_sidebar__static_list__item {
    position: relative;
    top: 10px !important;
}
.p-channel_sidebar .p-channel_sidebar__static_list__item[aria-expanded] {
    height: 0.75em;
    visibility: hidden;
}
/*.p-channel_sidebar .p-channel_sidebar__static_list__item[aria-level="2"] {
    background: darkgray;
}*/
.p-channel_sidebar i.p-channel_sidebar__edit_mode_checkbox {
    visibility: hidden;
    width: 10px;
}
.p-channel_sidebar div.p-channel_sidebar__user_avatar,
.p-channel_sidebar div.p-channel_sidebar__mpim_avatars
{
    margin-left: 2px;
}
.p-channel_sidebar .p-channel_sidebar__link.p-channel_sidebar__link--add-more-items {
    padding-left: 18px;
    margin-bottom: 8px;
}
}
