css:
@-moz-document url-prefix("https://spigotmc.org"), url-prefix("https://www.spigotmc.org/") {
/* Backgrounds */
body {
    background: #131719;
}
#logoBlock .spigot_colorOverlay, #navigation .navTabs, .breadcrumb, .uix_breadCrumb_toggleList li.toggleList_item, .navTabs .navTab.selected .tabLinks a:hover, .navTabs .navTab.selected .tabLinks a:focus, .sectionMain, .primaryContent, .PageNav a, input#QuickSearchQuery, #QuickSearch .primaryControls .uix_icon, .sidebar .section .primaryContent h3, .sidebar .section .secondaryContent h3, .profilePage .mast .section.infoBlock h3, .resourceListSidebar h3, .sidebar .section .secondaryContent, .discussionListItem, .discussionListItem:nth-child(even), .discussionList .sectionHeaders, .tabs li.active a, .tabs.noLinks li.active, .tabs li:hover a, .tabs.noLinks li:hover, .Menu .primaryContent, .Menu .secondaryContent, .resourceListSidebar .secondaryContent, .resourceListMain, .secondaryContent, .resourceListItem, .resourceListItem .resourceImage, .resourceListItem .resourceStats {
    background-color: #1b2022;
}
footer {
    background: #1b2022;
}
.navTabs .navTab.selected .tabLinks, .navTabs .navTab.selected .navLink, .mainContainer .mainContent, .mainContainer_noSidebar, .subHeading, .bbCodeBlock .type, .bbCodeBlock pre, .bbCodeBlock .code, .bbCodeBlock, .sectionFooter, .PageNav a:hover, .PageNav a.currentPage, .timeline-Tweet:hover, .TwitterCard-container--clickable:hover, .tabs li a, .tabs.noLinks li, .blockLinksList a:hover, .blockLinksList a:focus, .blockLinksList li.kbSelect a, .blockLinksList label:hover, .blockLinksList label:focus, .blockLinksList li.kbSelect label, .dataTable tr.dataRow th {
    background: #323639;
}

/* Colors */
.navTabs .navTab.selected .tabLinks a, .subHeading a, .subHeading a:hover, .primaryContent, .bbCodeBlock .type, .bbCodeBlock pre, .bbCodeBlock .code, .sectionFooter, .PageNav, .PageNav a, input#QuickSearchQuery, .sidebar .section .secondaryContent, .threadListItem .messageInfo *, .timeline-Widget, .timeline-Header-title, .titleBar h1, .discussionListItem:nth-child(even), .discussionListItem, .tabs li a, .tabs.noLinks li, .tabs li:hover a, .tabs.noLinks li:hover, .blockLinksList a, .blockLinksList label, .blockLinksList a:hover, .blockLinksList a:focus, .blockLinksList li.kbSelect a, .blockLinksList label:hover, .blockLinksList label:focus, .blockLinksList li.kbSelect label, .secondaryContent a, H\.secondaryContent a[href]:hover, #pageDescription, .resourceListMain, .Popup .PopupControl:hover, .Popup.PopupContainerControl:hover, .resourceListItem .main .version, a[href]:hover, .resourceInfo h1, .innerContent .customResourceFields dt, .textHeading, .xenForm .sectionHeader, .xenForm fieldset, .xenForm .formGroup, .dataTable tr.dataRow td, .dataTable tr.dataRow th, .searchResult .snippet a, .titleBar h1 em {
    color: white;
}
body .muted, body a.muted, body .muted a, .searchResult .meta, .searchResult .meta a {
    color: #ffffffa9;
}
.secondaryContent a {
    color: white !important;
}

/* Border Colors */
.navTabs .navTab.selected .tabLinks {
    border-color: transparent;
}
.sectionMain, .primaryContent, .bbCodeBlock .type, input#QuickSearchQuery, .discussionList .sectionHeaders, .tabs li a, .tabs.noLinks li, .tabs, .resourceListSidebar .secondaryContent, .resourceListMain, .textHeading, .xenForm .sectionHeader, .innerContent .updates ol, .xenForm fieldset, .xenForm .formGroup, .dataTable tr.dataRow td, .dataTable tr.dataRow th {
    border-color: #1b2022;
}
.hasTabLinks #navigation .navTabs .navTab.selected .navLink, .mainContainer .mainContent, .mainContainer_noSidebar, .subHeading, .articleItem .metaData, .bbCodeBlock, .PageNav a, .PageNav a:hover, .PageNav a.currentPage, .threadListItem, .discussionListItem, .discussionListItem:nth-child(even), .discussionListItem .stats dl, .secondaryContent, .resourceListItem, .actionFilterRow, .innerContent .customResourceFields.aboveInfo, .eventList li, html .searchResult {
    border-color: #323639;
}

/* Other */
.navTabs .navTab.selected .tabLinks a {
    text-shadow: none;
}
#navigation .navTabs .navTab.selected .navLink:before, #navigation .navTabs .navTab.selected .navLink:after {
    display: none;
}
}
