css:
@-moz-document regexp("http.*vcfed\\.org/forum.*") {
/* CSS overrides for VCFED vBulletin 5 theme */

/* height */
 #header .site-logo {
    padding-top: 6px;
    padding-bottom:6px;
}
/* width */
 #header, #channel-tabbar, #channel-subtabbar, .breadcrumbs-wrapper, .notices, #content, #footer, .b-top-menu__container {
    max-width: calc(100% - 32px);
    margin: auto;
}
@media only screen and (max-width:1279px) {
    #header, #channel-tabbar, #channel-subtabbar, .breadcrumbs-wrapper, .notices, #content, #footer, .b-top-menu__container {
        max-width: 100%;
    }
}
#breadcrumbs {
    margin:0;
    width: 100%;
    max-width:100%
}
/* colors */
 #channel-tabbar, #channel-tabbar .channel-tabbar-list li a, .conversation-toolbar-wrapper .conversation-toolbar {
    background: none 0 0, linear-gradient(to bottom, #3EB6A6, #14A693), #28AE9C;
}
.forum-list-container .category-header {
    background-color: #15A593
}
.forum-list-container .category-header .category, .conversation-toolbar-wrapper .conversation-toolbar {
    color: white !important;
}
.forum-list-container .forum-item, .forum-list-container .subforum-list, .blogmember-list .list-container .list-item, .sg-groups-list .sg-groups-list-container .list-item, .topic-list-container .topic-list tr {
    background: none 0 0, linear-gradient(to bottom, rgba(240, 240, 240, 0), rgba(240, 240, 240, 0) 77%, rgba(240, 240, 240, 1)), #fff;
}
.topic-list-container .topic-list .topic-item.sticky {
    background: linear-gradient(to bottom, rgba(240, 240, 240, 0), rgba(240, 240, 240, 0) 77%, rgba(215, 226, 231, 1)), #F2F6F8
}
/* links */
 a:hover {
    text-decoration: underline !important;
}
/* forum list */
 .forum-list-container .forum-list-header .header-lastpost, .forum-list-container .forum-item .lastpost {
    width: 36%;
}
.forum-list-container .forum-item td {
    padding: 8px 0
}
.forum-list-container .forum-item td, .subchannel-widget .forum-list-container .forum-item td {
    border-top-color: #555 !important;
    border-top-style:dotted !important;
}
/* forum view */
 .topic-list-container .topic-list .topic-item td {
    padding: 8px 12px;
}
.topic-list-container .topic-list .topic-item .cell-topic-image {
    padding: 8px;
}
a.topic-title {
    font-size: 110%
}
/* fonts */
 body {
    font-family: Tahoma, Calibri, Verdana, Geneva, sans-serif
}
.widget-tabs .widget-tabs-nav li, .widget-tabs .widget-tabs-nav li.ui-tabs-active, .forum-list-container .category-header .category, .ui-widget, .ui-widget button, .notifications-count, #channel-tabbar .channel-tabbar-list li a, #channel-tabbar .channel-tabbar-list li.current a, #channel-tabbar .channel-tabbar-list li.current a:hover, #channel-subtabbar .channel-subtabbar-list li a, #footer-tabbar ul.nav-list li a, #footer-copyright, #breadcrumbs, .list-container .list-header, .module-title, .module-title h1, .page-title-widget .module-title, .page-title-widget .module-title h1, .view-mode .canvas-layout-container .canvas-widget.widget-no-border > .widget-header .module-title, .view-mode .canvas-layout-container .canvas-widget.widget-no-border.widget-tabs > .widget-header.widget-tabs-nav .module-title, .b-comment-entry__textbox, .b-table__cell--header, .forum-list-container .forum-list-header, .b-button, .ui-widget .b-button, .cke_dialog_body .cke_dialog_contents .b-button, .forum-list-container .forum-item .cell-forum .forum-title, .b-post-control, .conversation-toolbar-wrapper .conversation-toolbar {
    font-family: Tahoma, Calibri, Verdana, Geneva, sans-serif !important;
}
.b-post__label, .b-post__content, .post-content, .OLD__post-content {
    font-family: Verdana, Arial, Tahoma, Calibri, Geneva, sans-serif !important;
}
.bbcode_container .bbcode_code, code {
    font-family: Inconsolata, "Andale Mono", "Monotype.com", "Courier New", Courier, monospace;
    font-size:1.4rem;
    line-height:1.45rem
}
/* bookface tw*tter */
 .b-sharing-menu {
    display:none;
}
/* post metadata */
 .b-post {
    background-color: #E2EDF3;
    border-color:#96B1C3
}
.b-meter__bar {
    background: #fff
}
.b-avatar--m {
    height:80px;
    width:80px;
    border: none;
}
.b-avatar--m > img {
    max-width: 80px !important;
    max-height:80px !important;
    margin:auto !important;
}
.b-userinfo {
    padding: 8px 6px 6px;
    line-height: 1.35rem;
}
.h-margin-bottom-l {
    margin-bottom: 6px;
}
.h-margin-top-xl {
    margin-top: 10px;
}
/* post content */

/*@media only screen and (min-width:768px) {*/
 .b-post__content .b-media {
    position: relative;
    top: -15px;
    left: -20px;
    width: calc(100% + 20px);
    padding: 4px 10px 6px 10px;
    margin-bottom: -15px;
    background: none 0 0, linear-gradient(to bottom, #3EB6A6, #14A693), #28AE9C;
    font-family: Tahoma, Calibri, Verdana, Geneva, sans-serif !important;
}
.b-post__content .b-media * {
    color: white !important;
}
/*}*/
 @media only screen and (max-width:767px) {
    .b-post__content .b-media {
        background: inherit;
        position: relative;
        top: -15px;
        left: -8px;
        width: 100%;
        padding: 4px 10px 6px 10px;
        margin-bottom: -15px;
        font-family: Tahoma, Calibri, Verdana, Geneva, sans-serif !important;
    }
    .b-post__content .b-media * {
        color: #656A6D !important;
    }
    .b-post__content .b-media h2 {
        color: #000 !important;
    }
}
.b-post__content .b-media .b-form-control__control {
    margin:0;
}
.b-post__timestamp {
    font-size: inherit;
}
/* quotes */
 .bbcode_container .bbcode_quote {
    background-color: #F2F6F8;
    border: 1px solid #96B1C3;
    border-radius: 4px;
}
}
