.c-exercise-table__grades-list-item.-passed {
    background-color: #00a651;
    color: #fff;
    border: solid 1px #00a651;
    white-space: nowrap;
    padding: 6px 12px;
    font-weight: 500;
    border-radius: 2px;
    cursor: pointer;
}
}

@-moz-document domain("idp.uitgeverij-deviant.nl") {
/* inlog scherm */
.container__content {
    background-color: #23272A;
}

.title {
    color: #dcddde;
}

.login-separator::before {
    content: "of";
    color: #dcddde;
    text-shadow: 0 0 0.25rem black, 0 0 0.25rem black, 0 0 0.25rem black, 0 0 0.25rem black;
}

/* main */

body, html {
    background: #2C2F33;
    color: #dcddde;
    --surface-shade: #0c0d0e;
}


input[type=email], input[type=url], input[type=search], input[type=tel], input[type=color], input[type=text], input[type=password], input[type=datetime], input[type=datetime-local], input[type=date], input[type=month], input[type=time], input[type=week], input[type=number], textarea {
    color: #858585;
    background-color: #23272A;
    border: 1px solid #0c0d0e;
}

/* width */
::-webkit-scrollbar {
  width: 15px;
}

/* Track */
::-webkit-scrollbar-track {
  background: #23272A; 
}
 
/* Handle */
::-webkit-scrollbar-thumb {
  background: #474c52; 
}

/* Handle on hover */
::-webkit-scrollbar-thumb:hover {
  background: #2f3337; 
}

}
