css:
@-moz-document url-prefix("https://meitav.viewtrade.com/secure/") {


body {
    font-family:'Rubik', sans-serif;
}
td.maincell {
    border:none;
    background-color: #2E2E2E;
    border-radius:10px;
}
input[type=text] {
     cursor: text;
    border:none;
    border-radius:2px;
  border-bottom: 2px solid grey;
  outline: 0;
  font-size: 1.2rem;
  color: white !important;
  background: transparent;
  transition: border-color 0.2s;
}




.findButton{
 font-size:1.1rem;
    padding:10 20;
}




tr.homemarketsSelected td, tr.gridTableHeader td{
 background:black;   
    
}


input:focus{
    outline:0;
    border:none;
    border-right:0;
     border-bottom: 2px;
  border-style: solid;
  font-weight: 400;
   border-width: 0 0 2px;
    
 border-image: linear-gradient(to right, #11998e,#30FF00);
  border-image-slice: 1;
    transition: all 0.2s
}


div.indexBox{
 background:#494949;   
    padding:14px;
    margin: 20px;
    border-radius:10px;
    box-shadow:0 10px 20px rgba(0,0,0,0.2);
     transition: all 0.25s
}



td.gridtableTextNews a{
 text-decoration:none;   
    
}

td.gridtableTextNews{

 border-bottom: 2px solid #414141;   
}

div.indexBox:hover{
    

    transform: translate(0.2%,-0.2%);
    box-shadow:0 5px 10px rgba(0,0,0,0.2);
    transition: all 0.25s
}

div.ribbon_menuItem{
    padding:10px;
    display:flex;
    align-items:center;
    height:25px;
    background-image:none;
/*     border-right: 2px solid rgba(0,0,0,0.2);
      border-bottom: 2px solid rgba(0,0,0,0.2); */
/*  background: linear-gradient(to top, #434343, #000000); */
}

table.ribbon_menu{
    border:none;
     position:absolute;
    top:0;
    left:0
}




div.ribbon{
    display:inline-block;
 margin-top:50px;   
    text-align:center;
                                            width:100%;
}


table.ribbon_menu tr td{
     background: linear-gradient(to top, #434343, #000000);
    border:none;

}

table.ribbon_menu tr {
    border:none;
}


div.ribbon_menuItem a {
    display:block;
    padding:10px;
    text-decoration:none;
    font-size:0.85rem;
}


div.ribbon_menuItem a :hover{
text-decoration: none;
    text-decoration-color: #30FF00;
}

div.ribbon_menuItem :hover{
font-weight:bolder;
   border-bottom: 3px #30FF00 solid;
}




td div.ribbon_menuItemCurrent{
    height:18px;
    background-image:none;
      border-bottom:  rgba(48, 255, 0,1) solid 10px;
     background: rgb(30, 30, 30);
        padding: 9px 15px;
}

td div.ribbon_menuItemCurrent a{

    font-size:bolder;
    color:white;
}


body{
background: #000000;  /* fallback for old browsers */
background: -webkit-linear-gradient(to right, #434343, #000000);  /* Chrome 10-25, Safari 5.1-6 */
background: linear-gradient(to top, #434343, #000000); /* W3C, IE 10+/ Edge, Firefox 16+, Chrome 26+, Opera 12+, Safari 7+ */

    
}

div.title{
display:none;
}

input[type=submit]{
    background:#26CC00;
    box-shadow: 0 10px 20px rgba(0,0,0,0.2);
    border:none;
    color:white;
    padding: 3px 15px;
    border-radius:10px;
    cursor:pointer;
     transition: all 0.2s
}

input[type=submit]:hover{
    color:#2E2E2E;
      box-shadow: 0 5px 10px rgba(0,0,0,0.2);
    background:#30FF00;
    transition: all 0.2s
}

table.aboveRibbon > tbody > tr > td:nth-child(1){
display:none;
}

.frontLastLogin{
color:#2E2E2E;
    font-size:0.6rem;
    margin-top:5px;

}

.mainLogo{
 border:none;   
}

.mainLogo img{
object-fit: cover;
    content:url(https://play-lh.googleusercontent.com/nVzc1DjH0ZYqr1OkhZELxV-nAf_ge76xl1vR-stg8MIIQXX_MXxowsyT5ub9V0v84HA);
     border:none; 
       width:150px;
    height:100px;
}


 td.gridtableSubTitle2 a{
