css:
@-moz-document domain("www.clickcritters.com") {
body {
    background: url(https://www.clickcritters.com//images/layout/traptor_sky/wallpaper2.png) left top repeat, url(https://i.imgur.com/gL6mD3C.png) left top repeat-x;
    background-image: url(https://www.clickcritters.com//images/layout/traptor_sky/wallpaper2.png), url(https://i.imgur.com/gL6mD3C.png);
    background-position-x: left, left;
    background-position-y: top, top;
    background-size: initial, initial;
    background-repeat-x: repeat, repeat;
    background-repeat-y: repeat, no-repeat;
    background-attachment: initial, initial;
    background-origin: initial, initial;
    background-clip: initial, initial;
    background-color: #227380;
}

#megaWrapper {
    background-image: none;
}



#megaHeader {
    background-image: none;
}

#imgHeaderLeft {
    display: block;
    -moz-box-sizing: border-box;
    box-sizing: border-box;
    background: url('https://i.imgur.com/kA1MjYL.png') no-repeat;
    width: 1200px;
    height: 125px;
    padding-left: 1200px;
    margin-left: auto;
    margin-right: auto;
}



#imgHeaderRight {
    height: 0px;
}

textarea {
    font-size: 10pt;
    font-family: Verdana;
}

td,
p,
body {
    font-size: 8pt;
    font-family: Verdana;
    text-align: center;
}

a:hover {
    color: #47636b;
    text-decoration: none;
}

#megaAd {
    width: 160px;
    background-color: #d2dfe1;
    vertical-align: top;
    margin-left: -50px;
}

#megaAd iframe {
    position: absolute;
    right: 0px;
    padding-top: 9px;
}

#megaTopNav,
#megaSearch {
    background-color: #47656b;
    background-image: url('https://i.imgur.com/94JjIvY.jpg');
}

#megaSidebar {
    background-color: #4e7c86;
}

#megaUserinfo {
    background-color: #96b3b8;
    background-image: url('https://i.imgur.com/X6bkmxU.jpg');
    background-repeat: repeat-x;
}


#megaContent,
#megaFooter {

    background-color: #93b9b9;
    /* The color of the background that is usually white is set here. White is #fff */
}

#megaFooter {
    padding-left: 15%;
}

#megaBottomLeft {
    background-color: #517c88;
}

#megaBottomLeftImg {
    background-image: url('https://i.imgur.com/No9zay4.png');
}

#megaBottomRight {
    width: 160px;
    background-color: #93b9b9;
}

#megaBottomRightImg {
    background-image: url('https://i.imgur.com/UyNoQjN.png')
}

h1 {
    background-color: #4ba5a8;
    border: 2px solid #0d5662;
}

#topNavLinks li ul.subnav {
    background: #4ba5a8;
    border: 2px solid #47696b;
}

#topNavLinks li ul.subnav li {
    margin: 0;
    padding: 0;
    border-top: 1px solid #0a404f;
    /*--Create bevel effect--*/
    border-bottom: 1px solid #0d3f4f;
    /*--Create bevel effect--*/
    clear: both;
    width: 170px;
    height: 20px;
}

#topNavLinks li ul.subnavi li a {
    color: #ebf8fc;
}

html #topNavLinks li ul.subnav li a:hover {
    background-color: #47646b;
    color: #ebf7fc;
}

div.forum {
    color: #47616b;
    background-image: url('https://i.imgur.com/WrDperO.jpg');
    background-repeat: repeat-x;
    background-color: #759ba3;
}

div.forum div {
    background-image: none;
}

div.forum a {
    color: #0f232a;
}

div.category {
    vertical-align: center;
    background-color: #47666b;
    background-image: url('https://i.imgur.com/94JjIvY.jpg');
    background-repeat: repeat-x;
}

div.category div {
    background-color: #93b9b9;
    background-image: none;
}

#forumtable tbody {
    background-color: #47626b;
}

#forumtable tr.unread {
    background-color: #b6d6dc;
}

#forumtable tr .off {
    background-color: #d8e5ea;
}

#forumtable tr a:hover {
    color: #fff;
    text-decoration: none;
}

#forumtable tr:hover {
    background-color: #85adb2;
}

form input {
    background-color: #e7f3f8;
}

textarea,
select,
input {
    background-color: #ebf9fc;
    font-family: verdana;
}

input[type=submit] {
    border-radius: 5px;
    background: linear-gradient(#ebf9fc, #a2bec7);
}

#mail_heading tr,
#mail_heading td {
    background-color: #81c7d3;
    border: solid 1px #043249;
}
}
