css:
/* YouTube Resized (for Mid-Resolutions)*/
/* Author: Feudal http://userstyles.org/users/26776 */
/* Almost entirely based on "Bigger YouTube" by confuciou */
/* http://userstyles.org/users/3062 */
/* and on "My Youtube Resizer for 1280 x 720 resolution" by blue2107 */
/* http://userstyles.org/users/7911 */

@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("youtube.com") {

#baseDiv { width: 1100px !important; }
#movie_player {width: 784px !important; height: 480px !important;}
#aboutExploreDiv, #dVidsDiv, #otherVidsCell {padding-left: 330px !important;}
#leaderboardAd {display: none !important;}
}


#vidTitle {
font-size: 23px !important;
height: 30px !important; 
margin-bottom: 10px !important; 
margin-top: 20px !important; 
}

#masthead {

height: 60px; 
left: 0px !important; 
margin-bottom: 0px !important; 
margin-top: 10px !important; 
}


 #footer, #copyright, #relatedVidsToggle, #promotedVidsContainer, #channelBrandCap, #channelVidsBody, #logoTagDiv, #channelBrandDiv, .hpContentBlock, #hpSideContent, #gNavDiv, #asDiv
 {

display: none !important;

}

#watch-this-vid-info { width: 784px !important; }
.watch-comment-body { width: 600px !important; }
#smallMastheadBottom, #leaderboardAd {display: none !important;}
.watch-discoverbox, #more_channel_videos {
  height: auto !important;
  max-height: 500px !important;

}

#body-column { width: 800px !important; }

.grid-view .video-cell {
  width: 130px !important;
  height: 180px !important;
}
.grid-view .video-clear-list {
  display:none !important;
}
.grid-view table, .grid-view tbody, .grid-view tr, .grid-view td {
  display: block !important;
}
#homepage-main-content {
  width: 750px !important;
}
#profile-main-content {
  width: 750px !important;
}
#profileVideos .browseGridView {
  width: 100% !important;
}
