 @-moz-document domain("fumired.ucontactcloud.com") {
   /* i really want this to be global */
tr.selected, .selected {
    
    
    background: linear-gradient(to right, #ff2300, #ff9d87e6)!important;
    /*background: linear-gradient(to bottom, #fb7f00, #f12711)!important;*/
}


tbody tr:hover {
    /* background: #2193b0; */
    /*background: linear-gradient(to bottom, #50deff, #2193b0);*/
        background: linear-gradient(to top, #57fd54d9, #c8ff44e0);
}

tr:hover td{color: #1b1818!important;
    font-size: 13px!important;
    font-weight: 600!important;}



thead tr th{padding: 7px 10px!important;}
thead tr {
        /*background: linear-gradient(to top, #258ea7c2, #46ccfff7);*/
            background: linear-gradient(to top, #035063bd, #0cb6dcf7);
}

.dataTables_scrollBody table thead tr {
    background: unset;}

.dataTables_scrollHeadInner table {width: 100%;}
body{background: linear-gradient(to right, #108032, #00a183)!important;}
.tile.color.transparent-black{background-color: #fff7f75c;
    background: linear-gradient(to bottom, #ffffff4f 0%,#e5e5e56b 100%);
    box-shadow: 0px 0px 4px 2px #1819195e;
    border: solid 1px #7cfffd70;}
.dataTables_filter{background: #07825f;
    padding: 2px 6px;
    border-radius: 4px 4px 0px 0px;}
.navbar-collapse{background: linear-gradient(181deg, #037450, #00230b);}
.dropdown-menu li a:hover {background-color: rgb(89 255 189);}
li.title {
    list-style: none;
}

.btn-verde {color: #f2f2f2; opacity: 0.8;background: linear-gradient(355deg, #0c6102, #3bc204f2);border: solid 1px #e0e0e0d1;}
.table.table-custom > thead > tr > th.sorting_asc{
    color: white!important;
}
.btn-danger {background: linear-gradient( 4deg, #a51010, #ff0e0e);}
  
}
