/*gmail*/
@-moz-document domain("mail.google.com") {

	div[style="float: right;"] div[style*="width: 230px"]{
	  width:350px!important;
	}

	div[style="float: right;"] div div div[style*="width: 225px"]{
	  width:345px!important;
	}
}


/*google*/
@-moz-document domain("google.com"), domain("google.it") {
	#ghead, 
	#sbl, 
	.fade, 
	#fctr{ 
	  opacity: 1 !important; 
	}
}


/* HATTRICK ******************************************************************* */
@-moz-document domain("hattrick.org"){

	body{
	  font-family: Tahoma !important;
	}


	/* lets remove splash image */
	img[src$="/Img/Illustrations/splash.png"]{
	  display:none!important;
	}

	textarea.notebook{
	  background:none!important;
	  width:100%!important;
	  height:310px!important;
	  padding:30px 10px 30px 5px!important;
	  font-size: 1.1em!important;
	  line-height:1.3em!important;
	  font-family:monospace!important;
	}

	div.ownerNotes textarea{
	  font-size: 1.15em!important;
	  line-height:1.4em!important;
	  font-family:monospace!important;
	  padding:5px 15px 5px 7px!important;
	  height:150px!important;
	}

	div#mainBody>div.mainBox>table>tbody>tr>td>a.inner[href="/Help/Supporter/"],
	div#mainBody>h3[class="feedItem"]{	  
	  display:none!important;
	}




}


@-moz-document domain("lupennino.mastertopforum.com"){

	div#fb,
	table#scambiolink{
	  display:none!important;
	}

}

@-moz-document domain("hattrickitalia.org") {
	div#ajax_chat_h, 
	div#ajax_chat,
	.empty-table>tbody>tr>td#var_width,
	#c1_f,
	#c5_f,
	#c7_f,
	#birthday{
	  display:none!important;
	}
}

@-moz-document domain("htita.it") {
	li#cat25,
	li#forum82,
	li#forum14,
	li#forum42,
	li#forum92,
	li#forum95,
	li#cat56,
	div#wgo_legend,
	div#wgo_stats,
	div#wgo_birthdays,
	img[src="http://www.r2m.it/pics/banner/htita_r2m.gif"]{
	  display:none!important;
	}


	.threadbit .threadstatus {	  
		background-image:url(http://nrgjack.altervista.org/_altervista_ht/style/icons/ball-icon.png)  !important;
		opacity:0.25!important;
	}

	.new  .threadstatus {
		opacity:1!important;
	}

	.threadbit.dot .threadstatus {
		border-left:solid 8px green  !important;
	}

	.threadbit .sticky .threadstatus {
		background-image:url(http://nrgjack.altervista.org/_altervista_ht/style/icons/trophy-icon.png)  !important;
	}

	img[src*="forum_new-48.png"]{
		padding: 48px 0 0 48px!important;
		width: 0 !important;
		height: 0 !important;
		background-image: url(http://nrgjack.altervista.org/_altervista_ht/style/icons/documents-yellow-edit-icon.png)  !important;
	}

	img[src*="forum_old-48.png"]{
		padding: 48px 0 0 48px!important;
		width: 0 !important;
		height: 0 !important;
		background-image: url(http://nrgjack.altervista.org/_altervista_ht/style/icons/documents-white-icon.png)  !important;
	}

	div.has_ad{
		margin-right:auto!important;
	}

	body{
		margin:0!important;
	}
	
	li#nonotifications{
		display:block!important;
	}

	.postbody {
		border-left:1px solid #ccc!important;
		border-bottom:1px solid #ccc!important;
	}

	.postbitlegacy {
		border:1px solid #ccc!important;
	}

}


@-moz-document domain("mentalita.forumcommunity.net") {
	#bacheca,
	#fc341179,	/* DIV sezioni speciali*/
	#fc7332280,	/* DIV coppa  mentalita*/
	#fc620828,	/* DIV fantacalcio*/
	#fc297799,	/* DIV Pubblicita*/
	#f2883281,	/* mentalita records*/
	#f167278,	/* Sgambate e tornei a calcetto*/
	#f167275,	/* quelli che il calcio*/
	#f5772995,	/* aspettando sud africa*/
	#f707427,	/* sala scommesse*/
	#f843816,	/* streaming tv*/
	#fotteruno,
	#fotterdue,
	#fottertre,
	div.stats{
	  display:none!important;
	}

	tr>td.bb>div>br{
	  line-height:1px!important;
	}
		
	td.msg_main>table.row3>tbody>tr>td>a>img{
	  max-width:50px!important;
	}
}

@-moz-document domain("hattrick.org.mx") {
	#cabecera,#base{
	  display:none!important;
	}
}

