css:
@-moz-document domain("kovnet.nl") {
img {
    max-width: 90vw;
    max-height: 90vh;
}

#lightbox-image {
    max-width: 100%;
    max-height: 90vh;
}

#lightbox-container-image-box {
    height: auto !important;
}

#lightbox-container-image-box,
#lightbox-container-image-data-box {
    max-width: 90vw;
    max-height: 90vh;
}

#lightbox-container-image-data-box {
    max-width: calc(90vw - 20px);
}

#jquery-lightbox {
    top: 40px !important;
    position: fixed !important;
}

#lightbox-nav a {
    height: 100% !important;
}
}
