css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain(reddit.com) {

table#topbar {
	margin-bottom: 35px !important;}
	
tr.oddRow, tr.evenRow {
	height: 20px !important;}
	
p.menu {
	line-height: 30px !important; 
	color: #000 !important; 
	padding-bottom: 10px !important;}
	
a:link.title {
	font-family: Helvetica, sans-serif !important; 
	color: #1462C1 !important; 
	font-size: 14px !important;}
	
a:visited.title {
	font-family: Helvetica, sans-serif !important; 
	color: #999 !important; 
	font-weight: normal !important; 
	font-size: 14px !important;}
	
a:hover.title {
	font-family: Helvetica, sans-serif !important; 
	color: #900 !important; 
	font-size:14px !important;}
	
a:active.title {
	font-family: Helvetica, sans-serif !important; 
	color: #000 !important; 
	font-size: 14px !important;}
	
.link {
	font-family: Helvetica, sans-serif !important; 
	margin: 0pt !important;
	border-bottom: 1px solid #dddddd !important;
	padding: 4px 0px !important;}
	
.link:hover {
	font-family: Helvetica, sans-serif !important; 
	background: #f5f5f5 !important;}
	
.link .rank {
	font-family: Helvetica, sans-serif !important; 
	font-size: 14px !important;}
	
.midcol {
	background:transparent !important;}

.login-form-side {
	border:0px dotted gray !important;
	margin-top:10px !important;}
	
# {
	display: none !important;}
	
.tagline {
	color: #666 !important;}
	
.comment .child {
	margin-top: 10px !important;}
	
.comment .author {
	color: #1462C1 !important;}
	
.comment .entry {
	padding: 3px 5px 3px !important; 
	border-bottom: 0px solid #ddd !important;
	border-left: 2px solid #ddd !important;}
	
.comment .entry:hover {
	background: #f5f5f5 !important;}
	
.even, .odd {
	border-left: 0px dotted #ddd !important;}
	
#sr-header-area {
	background: #222 !important;
	border: 0px !important;
	color :#777 !important;}
	
#sr-header-area a {
	color: #777 !important;}
	
#sr-more-link {
	background: #222 !important;
	color: #777 !important;}
	
.dropdown.srdrop .selected {
	color: #777 !important;}
	
.last-clicked {
	border: 0px !important; 
	border-left: 2px solid red !important; 
	border-bottom: 1px solid #dddddd !important;}
	
.organic-listing {
	border: 0px !important;}
	
.arrow.upmod { 
	background-image: url(http://www.reddit.com/static/aupmod.gif) !important;}
	
.arrow.downmod{ 
	background-image: url(http://www.reddit.com/static/adownmod.gif) !important;}
	
.md {-moz-border-radius: 5px !important;}

.formtab{ color: red !important; }

.link-desc {display: none !important;}

.roundfield {
  -moz-border-radius: 0px !important; 
  border: 1px dotted black !important;
  background-color: #CEE3F8 !important;
  font-size: small !important;

}
}
