css:
@-moz-document domain("backwards-compatible.com") {
a {
    color: #4a4ffb;
}
.search-results {
    background: rgb(33 33 33);
}
.search-func {
    box-shadow: 0 5px 20px rgb(0 0 0 / 66%);
    color: #9c9c9c;
    background: rgb(16 16 16);
}
A .game-wrap {
    box-shadow: 0 5px 20px rgb(0 0 0 / 42%);
    color: #d6d6d6;
    background: #101010;
}
.full-search INPUT#q {
    border: 1px solid rgba(0, 0, 0, 0.100);
    color: #e4e4e4;
    background-color: #222;
}
.full-search SELECT {
    border: 1px solid #4a4ffb;
    color: #9a9a9a;
    background-color: #222;
}
.not-listed A {
    background: #99c69a;
    color: #191919;
}
.title-info {
    background: #212121;
}
.info-right {
    background: #161616;
    box-shadow: 0 5px 20px rgb(0 0 0 / 40%);
}
.info-left {
    background: #161616;
    box-shadow: 0 5px 20px rgb(0 0 0 / 40%);
}
H3.tab {
    background: #161616;
    color: #d2d2d2;
}
.info-table {
    border: 1px solid #161616;
    background: #333;
}
.res-head,
.fps-head {
    background: #333!important;
    color: #dadada;
}
.info-table DIV {
    background: #333;
    border-bottom: 1px solid #E0E0E0;
    color: #d2d2d2;
}
.perf-imp {
    background: #333;
    border: 1px solid #333;
    color: #d2d2d2;
}
.info-right p {
    color: #d2d2d2;
}
.commento-root textarea {
    background: #333;
    color: #dcdcdc;
}
.h-list {
    background: rgb(33 33 33);
}
.description-box {
    box-shadow: 0 5px 20px rgb(0 0 0 / 40%);
    color: #afafaf;
    background: #161616;
}
#ajaxSearch {
    background: #161616;
}
#ajaxSearch A.ajaxSearch_close {
    background: #4a4ffb;
}
.ajaxSearch_body .notice {
    color: #d2d2d2;
}
.result-wrap {
    color: #d6d6d6;
}
.h-list H1 I {
    color: rgb(74 79 251);
}
SECTION.footer {
    background: #212121;
    color: #ffffff;
}
.footer IMG {
    filter: grayscale(0%) invert(0%);
    opacity: 0.5;
}
.main-nav {
    background: #282b8e;
}

::-webkit-scrollbar-thumb {
    background: #4a4ffb;
}
::-webkit-scrollbar-corner {
    background: #000;
}
::-webkit-scrollbar {
    width: 8px;
    height: 8px;
    background: #191919!important;
}
}
