css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("pkpolitics.com") {

body{
background-color: #333333 !important;
}

#wrapper, #featuredright .head, #otherblogs .head, #recentcomments .head
,#videos .head, #newsviews .head{
background-color: black !important;
border-top-color:black !important;
}

body {
background-image: none !important;
}




body #topnav a{
color:black !important;
font-family:"impact" !important;
font-size:17px !important;
text-decoration:underline !important;


}

#featuredright ul li a{
color: white !important;
text-decoration: none !important;
}

#block_2 img,#adsense,#recentcomments li,#recentcomments .head,
#videos ul li p{
display:none !important;
}

#login,#login .form{
background-color: black !important;
border-top-color:black !important;
}

#videos li:hover{
background-color:black !important;

}

#videos li a:visited{
text-decoration:line-through !important;
}

#comments .commenttext, .commenttext p{
background-color:black !important;

color:#C0C0C0 !important;
}

#comments .commenttext cite strong{
font-size:20px !important;
color:white !important;
}

#featured blockquote, #featured p,#featued .wp-polls strong,
#featured .commenttext{
color: white !important;
}





}
