css:
@-moz-document domain("www.nitrotype.com") {
body
  {
    background-color: #2d2e2e;
    overflow-y: scroll;
    background: none;
    overflow-x: hidden;
    position: relative;
  }

  .card--grit
  {
    background-image: url("https://cdn.discordapp.com/attachments/780205322375397396/835606416709386290/20210424_Devvy.png");
    background: fixed;
  }

  html
  {
    background: fixed url("https://cdn.discordapp.com/attachments/780205322375397396/835606416709386290/20210424_Devvy.png") no-repeat, #2d2e2e;
    box-sizing: border-box;
    color: #2d2e2e;
    font-family: "Montserrat", sans-serif;
    font-size: 16px;
    font-weight: 300;
    line-height: 1.6;
    background-size: 100%;
  }

 .bg--gradient {
    background: fixed;
}

  .btn--primary
  {
    background: #0c0c0c;
    color: #ea01ee;
  }

  .theme--pDefault .profile-title
  {
    color: #e908ff;
    text-shadow: 0 2px 2px rgba(2, 2, 2, 0.25);
  }

  .theme--pDefault .profile-levelNum
  {
    color: #0ae8e0;
    text-shadow: 0 2px 2px rgba(2, 2, 2, 0.25);
  }
  .tc-t {
    color: #09f0e3;
  }

  .theme--pDefault .profile-body
  {
    color: #ad06ec;
  }
}

@-moz-document domain("nitrotype.com") {
.theme--pDefault .profile-bg
{
    background: url("https://cdn.discordapp.com/attachments/780205322375397396/835608908789186630/20210424_Devvy_2.png") top left repeat, linear-gradient(0deg,#2d2e2e 60%, #fff 100%);
    color: #d3d3d3;
    background-size: 100%
}

.theme--pDefault .profile-bgHead
{
    background: url("https://cdn.discordapp.com/attachments/780205322375397396/835608908789186630/20210424_Devvy_2.png") top left repeat, linear-gradient(0deg,#2d2e2e 60%, #fff 100%);
    color: #d3d3d3;
    background-size: 100%
}

.theme--pDefault.is-gold .profile-bg
{
    background: url("https://cdn.discordapp.com/attachments/780205322375397396/835608908789186630/20210424_Devvy_2.png") top left repeat, linear-gradient(0deg,#2d2e2e 60%, #fff 100%);
    color: #d3d3d3;
    background-size: 100%
}
.card--a
{
    background-color: #fff0;
    color: #fff;
}

.dash-copy
  {
    color: #a603fd;
    font-family: "Roboto Mono", "Courier New", Courier, "Lucida Sans Typewriter", "Lucida Typewriter", monospace;
    font-size: 28px;
  }
.dash-copyContainer
  {
      background: #000;
    -webkit-box-shadow: 0px 1px 157px 10px rgba(0,0,0,0.78);
    box-shadow: 0px 1px 157px 10px rgba(0,0,0,0.78);
    border-radius: 5px;
    flex: 1;
    overflow: hidden;
    padding: 15px;
    display: flex;
  }

  .dash-copy
  {
    color: #a603fd;
    font-family: "Roboto Mono", "Courier New", Courier, "Lucida Sans Typewriter", "Lucida Typewriter", monospace;
    font-size: 28px;
  }

  ::selection
  {
    background-color: #1976c6;
    color: white;
  }

  .bg--gradient {
    background: fixed;
  }

  .theme--pDefault .profile-bg
  {
    background: url("https://cdn.discordapp.com/attachments/780205322375397396/835608908789186630/20210424_Devvy_2.png") top left repeat, linear-gradient(0deg, #2d2e2e 100%);
    color: #d3d3d3;
  }

  .btn--inky, .btn--inky:focus:not(:hover)
  {
    color: #ffffff;
  }

  .theme--pDefault .profile-bgHead
  {
    background: url("https://cdn.discordapp.com/attachments/780205322375397396/835608908789186630/20210424_Devvy_2.png") top left repeat, linear-gradient(0deg, #2d2e2e 100%);
  }

  .theme--pDefault.is-gold .profile-bg
  {
    background: url("https://image.shutterstock.com/image-photo/3d-wallpaper-beautiful-flower-background-260nw-1385636237.jpg") top left repeat, linear-gradient(0deg, #2d2e2e 100%);
  }

  .theme--pDefault.is-guest .profile-bgHead
  {
    background: #fff0;
  }
.btn--primary {
    background-image: url("https://store.hp.com/app/assets/images/uploads/prod/25-best-hd-wallpapers-laptops159561982840438.jpg");
    color: #fff;
    background-size: 100%;
    background-color: #000 !important;
   
}
.btn--secondary {
    background-image: url("https://store.hp.com/app/assets/images/uploads/prod/25-best-hd-wallpapers-laptops159561982840438.jpg");
    color: #fff;
    background-size: 100%;
        background-color: #000 !important;


}
.growl--a {
    background-image: url("https://cdn.discordapp.com/attachments/780205322375397396/835608908789186630/20210424_Devvy_2.png");
    border-color: rgba(2, 2, 2, 0.2);
    background-size: 100%;
    color: #fff;
}
path:first-child, path:nth-child(7) {
    fill: #c400ff;
}
.profile-carContainer.card.card--a.card--shadow-l {background-color:#fff0;}
.raceResults-awards {background:#fff0;}
.raceResults-close.raceResults-close--minimizer {background:#dfd612;}
.g.raceResults-grid {background:#fff0;}
.dash-content {background:url("https://512pixels.net/downloads/macos-wallpapers-thumbs/10-5--thumb.png");}
.footer-nav
{
    background:url(https://images.unsplash.com/photo-1511447333015-45b65e60f6d5?ixlib=rb-1.2.1&ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=1000&q=80)
}
.footer-copyright
{
    background:url(https://images.unsplash.com/photo-1511447333015-45b65e60f6d5?ixlib=rb-1.2.1&ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=1000&q=80 )
}
.nav-list
