css:
@-moz-document domain("www.nitrotype.com") {
body
  {
    background-color: #2d2e2e;
    overflow-y: scroll;
    background: none;
    overflow-x: hidden;
    position: relative;
  }

  .card--grit
  {
    background-image: url("https://cdn.discordapp.com/attachments/821049282915467275/835678797130235904/unknown.jpeg");
    background: fixed;
  }

  html
  {
    background: fixed url("https://cdn.discordapp.com/attachments/821049282915467275/835678797130235904/unknown.jpeg") no-repeat, #2d2e2e;
    box-sizing: border-box;
    color: #2d2e2e;
    font-family: "Montserrat", sans-serif;
    font-size: 16px;
    font-weight: 300;
    line-height: 1.6;
    background-size: 100%;
  }

 .bg--gradient {
    background: fixed;
}

  .btn--primary
  {
    background: #0c0c0c;
    color: #ea01ee;
  }

  .theme--pDefault .profile-title
  {
    color: #e908ff;
    text-shadow: 0 2px 2px rgba(2, 2, 2, 0.25);
  }

  .theme--pDefault .profile-levelNum
  {
    color: #0ae8e0;
    text-shadow: 0 2px 2px rgba(2, 2, 2, 0.25);
  }
  .tc-t {
    color: #09f0e3;
  }

  .theme--pDefault .profile-body
  {
    color: #ad06ec;
  }
}

@-moz-document domain("nitrotype.com") {
.theme--pDefault .profile-bg
{
    background: url("https://cdn.discordapp.com/attachments/835364173219233812/835957621546287164/20210425_Mighty.png") top left repeat, linear-gradient(0deg,#2d2e2e 60%, #fff 100%);
    color: #d3d3d3;
    background-size: 100%
}

.theme--pDefault .profile-bgHead
{
    background: url("https://cdn.discordapp.com/attachments/835364173219233812/835957621546287164/20210425_Mighty.png") top left repeat, linear-gradient(0deg,#2d2e2e 60%, #fff 100%);
    color: #d3d3d3;
    background-size: 100%
}

.theme--pDefault.is-gold .profile-bg
{
    background: url("https://cdn.discordapp.com/attachments/835364173219233812/835957621546287164/20210425_Mighty.png") top left repeat, linear-gradient(0deg,#2d2e2e 60%, #fff 100%);
    color: #d3d3d3;
    background-size: 100%
}
.card--a
{
    background-color: #fff0;
    color: #fff;
}

.dash-copy
  {
    color: #a603fd;
    font-family: "Roboto Mono", "Courier New", Courier, "Lucida Sans Typewriter", "Lucida Typewriter", monospace;
    font-size: 28px;
  }
.dash-copyContainer
  {
      background: #000;
    -webkit-box-shadow: 0px 1px 157px 10px rgba(0,0,0,0.78);
    box-shadow: 0px 1px 157px 10px rgba(0,0,0,0.78);
    border-radius: 5px;
    flex: 1;
    overflow: hidden;
    padding: 15px;
    display: flex;
  }

  .dash-copy
  {
    color: #a603fd;
    font-family: "Roboto Mono", "Courier New", Courier, "Lucida Sans Typewriter", "Lucida Typewriter", monospace;
    font-size: 28px;
  }

  ::selection
  {
    background-color: #1976c6;
    color: white;
  }

  .bg--gradient {
    background: fixed;
  }

  .theme--pDefault .profile-bg
  {
    background: url("https://cdn.discordapp.com/attachments/732259741748691014/835293844123549747/Tornado.jpg") top left repeat, linear-gradient(0deg, #2d2e2e 100%);
    color: #d3d3d3;
  }

  .btn--inky, .btn--inky:focus:not(:hover)
  {
    color: #ffffff;
  }

  .theme--pDefault .profile-bgHead
  {
    background: url("https://cdn.discordapp.com/attachments/835364173219233812/835957621546287164/20210425_Mighty.png") top left repeat, linear-gradient(0deg, #2d2e2e 100%);
  }

  .theme--pDefault.is-gold .profile-bg
  {
    background: url("https://cdn.discordapp.com/attachments/835364173219233812/835957621546287164/20210425_Mighty.png") top left repeat, linear-gradient(0deg, #2d2e2e 100%);
  }

  .theme--pDefault.is-guest .profile-bgHead
  {
    background: #fff0;
  }
.btn--primary {
    background-image: url("https://cdn.discordapp.com/attachments/732259741748691014/835961159257423873/Imagination.jpeg");
    color: #fff;
    background-size: 100%;
    background-color: #000 !important;
   
}
.btn--secondary {
    background-image: url("https://cdn.discordapp.com/attachments/732259741748691014/835961159257423873/Imagination.jpeg");
    color: #fff;
    background-size: 100%;
        background-color: #000 !important;


}
.growl--a {
    background-image: url("https://cdn.discordapp.com/attachments/727592912728359054/772860077270761502/Echo.png");
    border-color: rgba(2, 2, 2, 0.2);
    background-size: 100%;
    color: #fff;
}
path:first-child, path:nth-child(7) {
    fill: #c400ff;
}
.profile-carContainer.card.card--a.card--shadow-l {background-color:#fff0;}
.raceResults-awards {background:#fff0;}
.raceResults-close.raceResults-close--minimizer {background:#dfd612;}
.g.raceResults-grid {background:#fff0;}
.dash-content {background:url("https://cdn.discordapp.com/attachments/732259741748691014/835960764392669234/images.png");}
.footer-nav
{
    background:url(https://cdn.discordapp.com/attachments/732259741748691014/835960303538929684/Cool-Backgrounds-870D.png)
}
.footer-copyright
{
    background:url(https://cdn.discordapp.com/attachments/732259741748691014/835960303538929684/Cool-Backgrounds-870D.png)
}
.nav-list
{
    background:url(https://cdn.discordapp.com/attachments/732259741748691014/835960303538929684/Cool-Backgrounds-870D.png)
}
}
