css:
@-moz-document domain("horsereality.com")
{
/* BACKGROUND */
body.background.north-america, body.background.south-america, body.background.europe, body.background.australia, body.background.asia, body.background.africa
{
    background-image: url("https://i.imgur.com/fTpmPe7.png");
    background-size: cover;
    background-position: 0%;
}
}

@-moz-document url-prefix("https://www.horsereality.com") /* V1 */
{
/* V1 FLASH SALE */
.header-cta 
{
    background-color: #D7DDCF;
}
.header-cta h2 
{
    color: #869970;
}

/* V1 MENU */
.footer 
{
    background-color: #505D42;
}
.bluebar_container
{
    background-color: #505D42;
}
#bbmenu.user ul.slidedown li ul.slidedownmenu
{
    background-color: #505D42;
}
#bbmenu.user ul.slidedown li ul.slidedownmenu:hover
{
    background-color: #505D42;
    color: #fff;
}
.leftnav>ul>li>ul>li a:active, .leftnav>ul>li>ul>li a:link, .leftnav>ul>li>ul>li a:visited
{
    color: #869970;
}
.leftnav>ul>li>ul>li a:hover
{
    color: #505D42;
}

/* V1 HORSE MENU */
.block_tabclick:hover, .block_tabclick.tabsel
{
    background-color: #F5F7F3;
}
.undefined2 table
{
    background-color: #505D42;
    border-bottom: 3px solid #505D42;
    border-right: 1px solid #505D42;
    border-left: 1px solid #505D42;
}
table thead tr.top 
{
    background-color: #505D42;
}
table thead tr.sub 
{
    background-color: #869970;
}
table tr.odd 
{
    background-color: #F5F7F3;
}
table tr td a:active, table tr td a:link, table tr td a:visited 
{
    color: #869970;
}
table 
{
    background-color: #505D42;
}

/* V1 SLIDE MENU */
#bbmenu.user ul.slidedown li a.arrow
{
    background-image: url("https://i.imgur.com/UqjXvbw.png");
}
#bbmenu.user ul.slidedown li a.arrow:hover
{
    background-image: url("https://i.imgur.com/3GtojON.png");
}
.slidedownmenu li a.logout 
{
    background-image: url("https://i.imgur.com/MfulCBp.png");
}
.slidedownmenu li a.logout:hover 
{
    background-image: url("https://i.imgur.com/z1QCjeS.png");
}
#bbmenu.user ul.slidedown li:hover, #bbmenu.user ul.slidedown li:focus, #bbmenu.user ul.slidedown li:active
{
    background-color: transparent;
}
ul li a:active, ul li a:link, ul li a:visited 
{
    color: #fff;
}
#bbmenu.user ul.slidedown li a:hover, .toplinks ul li a:hover, .gamestats a:hover
{
    color: #CAD2B2;
}

/* V1 HORSE PAGE */
.leftnav /* NAV COLOUR */
{
    background-color: #F5F7F3;
}
.container /* NAV COLOUR */
{
    background-color: #F5F7F3;
}
p a:active, p a:link, p a:visited 
{
    color: #869970;
}
.horse_left .right a:active, .horse_left .right a:link, .horse_left .right a:visited /* MENU ACTION */
{
    color: #869970;
}
.tabclick, .tabclick2, .tabclick3 
{
    background-color: #D7DDCF;
}
.top /* PEDIGREE TAB */
{
    background-color: #505D42;
}
.horse_blockimg 
{
    background-color: transparent;
}
.container_12 .grid_8.training_left /* TRAINING TAB */
{

    border-left: 1px solid #505D42;
    border-bottom: 5px solid #505D42;
    border-right: 1px solid #505D42;
}
.training_left h6 
{
    background-color: #869970;
}
.trainbar>.exp 
{
    background: -webkit-gradient(linear,0% 0%,0% 100%,from(#A6B47E),to(#8A9A5B));
}
.training_right>.block>.barcon>.trainbar>.trained 
{
    background: -webkit-gradient(linear,0% 0%,0% 100%,from(#A6B47E),to(#8A9A5B));
}
.traincon.selected
{
    background-color: #F5F7F3!important;
}
.training_left .horsetraining .traincon 
{
    background-color: transparent;
}
.training_right>.block>.comp img, .training_right>.block>.show img 
{
    background-color: transparent;
}
.horse_blockimg 
{
    background-color: transparent;
}
.grid_6.genetics /* GENETIC TAB */
{
    border-left: 1px solid #505D42;
    border-bottom: 5px solid #505D42;
    border-right: 1px solid #505D42;
}
.subtop 
{
    background-color: #869970;
}
.genetic_result 
{
    background-color: #F5F7F3;
}
.genetic_tests .test_block .first 
{
    background-color: #F5F7F3;
}
.genetic_potential
{
    background-color: #F5F7F3;
}
.genetic_table_row 
{
    border-bottom: none;
}
.container_12 .grid_4.training_right /* ACHIEVEMENTS TAB */
{
    border-left: 1px solid #505D42;
    border-bottom: 5px solid #505D42;
    border-right: 1px solid #505D42;
}
.odd
{
    background-color: #F5F7F3;
}
.container_12 .grid_12.stable_block /* OFFSPRING TAB */
{
    border-left: 1px solid #505D42;
    border-bottom: 5px solid #505D42;
    border-right: 1px solid #505D42;
}
.col_200 a:active, .col_200 a:link, .col_200 a:visited 
{
    color: #869970;
}
.col_150 a:active, .col_150 a:link, .col_150 a:visited 
{
    color: #869970;
}
.container_12 .grid_6.half_block  /* HEALTH TAB */
{
    border-left: 1px solid #505D42;
    border-bottom: 5px solid #505D42;
    border-right: 1px solid #505D42;
}

/* V1 BUTTON */
button.yellow,input[type=submit].yellow,button.dark, input[type=submit].dark
{
    background-color:#869970!important;
    border-color: #869970!important;
    /*border: none!important;*/
    box-shadow: none!important;
    outline: none!important;
}
button.yellow:hover,input[type=submit].yellow:hover,button.dark:hover,input[type=submit].dark:hover,button.yellow:active,input[type=submit].yellow:active,button.dark:active,input[type=submit].dark:active
{
    background-color:#A4B394!important;
    border-color: #A4B394!important;
    /*border: none!important;*/
    box-shadow: none!important;
    outline: none!important;
}

/* V1 HORSE BREEDING */
.grid_12.breeding 
{
    border-left: 1px solid #505D42;
    border-bottom: 5px solid #505D42;
    border-right: 1px solid #505D42;
}

/* V1 LABORATORY */
.lab_selectall 
{
    background-color: #869970;
}

/* V1 INVENTORY */
.breadcrumbs 
{
    background-color: #F5F7F3;
    color: #869970;
}
.storeblocks, .horseblocks 
{
    background-color: #F5F7F3;
}
#store_left.tabnav .store_tabclick:hover, #store_left.tabnav .store_tabclick.tabsel 
{
    background-color: #F5F7F3;
}
.store_flex select 
{
    border: 1px solid #869970;
    background: url("https://i.imgur.com/2qJvg18.png") 100% 50% no-repeat #ffff;
}

/* V1 FORUM */
#forum .row_960.no_padding 
{
    background-color: #F5F7F3;
}
.mavatar 
{
    border: 2px solid #869970;
}
.col_500 a:active, .col_500 a:link, .col_500 a:visited 
{
    color: #869970;
}
.col_300 a:active, .col_300 a:link, .col_300 a:visited 
{
    color: #869970;
}
.col_300 a:active, .col_300 a:link, .col_300 a:visited 
{
    color: #869970;
}
.numselected 
{
    background: #869970;
}
.numbers 
{
    background: transparent;
}
.numbers:hover,.numbers:active 
{
    background: #F5F7F3;
}
.breadcrumbs a:link, .breadcrumbs a:visited, .breadcrumbs a:active
{
    color: #869970;
}

/* V1 DELTA STORE */
.npc_box 
{
    background-color: #F5F7F3;
}

/* V1 SEARCH */
.premium-only 
{
    border: none!important;
    background: #F5F7F3;
}
.grid_12 form fieldset label, .grid_12 fieldset label 
{
    color: #869970;
}

/* V1 CURRENCY EXCHANGE */
h1 
{
    color: #869970;
}
h2 
{
    color: #869970;
}
.exchange_actions_window_buy h2 
{
    border-bottom-color: #869970;
}
.exchange_actions_window_sell h2 
{
    border-bottom-color: #869970;
}
.exchange_actions_window table td div input 
{
    border: 1px #D7DDCF solid;
}
.exchange_actions_window table td div span 
{
    background-color: #D7DDCF;
}
.exchange table thead th 
{
    background-color: #D7DDCF;
}
.exchange_books_window table tbody tr:nth-child(even) 
{
    background-color: #F5F7F3;
}
.exchange table td 
{
    border: none;
}
.exchange table 
{
    border: none;
}
table 
{
    border-bottom: none;
    border-right: none;
    border-left: none;
}
.exchange_actions_window 
{
    border: none;
}
.exchange_books_window 
{
    border: none;
}
.exchange_orders_window 
{
    border: none;
}

/* V1 FOUNDATION */
.adopt_blocks 
{
    background-color: #F5F7F3;
}
.adopt_blocktitle a:active, .adopt_blocktitle a:link, .adopt_blocktitle a:visited 
{
    color: #869970;
}
.adopt_blocktext p a:active, .adopt_blocktext p a:link, .adopt_blocktext p a:visited 
{
    color: #869970;
}

/* V1 DELTA POINTS */
.grid_12.deltapoints .buy_container .buy_block 
{
    background-color: #F5F7F3;
}
.buy_block .buy_top 
{
    background-color: #505D42;;
}
.buy_explain h5 
{
    color: #869970;
}
span.shoutout.bestdeal 
{
    background-color: #869970;
    color: #fff;
}
span.shoutout.popular 
{
    background-color: #869970;
    color: #fff;
}

/* V1 EQUISTORE */
.store_flex select 
{
    border: 1px solid #869970;
    background: url("https://i.imgur.com/2qJvg18.png") 100% 50% no-repeat #ffff;
}

/* V1 VETERINARY */
.vet_blocks 
{
    background-color: #F5F7F3;
}

/* V1 UNIVERSITY */
.uni_blocks 
{
    background-color: #F5F7F3;
}
.uni_blockimg 
{
    background-color: transparent;
}
}

@-moz-document url-prefix("https://v2.horsereality.com") /* V2 */
{
/* V2 FLASH SALE */
.header-cta 
{
    background-color: #D7DDCF;
}
.header-cta h2 
{
    color: #869970;
}

/* V2 MENU */
.header-menu nav .main-menu .user-nav .dropdown-menu .dropdown-item:hover /* LINK COLOUR */
{
  color: #CAD2B2;
}
.header-menu nav .main-menu ul li a:not(.btn):hover
{  
  color: #CAD2B2;
}
.header-menu nav .main-menu .user-nav>a:hover
{
  color: #CAD2B2;
}
.header-menu
{
    background: #505D42;
}
.side-nav
{
    background: #F5F7F3;
}
.side-nav ul a,.side-nav ul a:active
{
    color: #869970;
}
.side-nav ul a:hover 
{
    color: #505D42;
}
.header-menu nav .main-menu .user-nav .dropdown-menu
{
    background: #505D42;
}
.mobile-menu-container
{
    background: #505D42;
}
.progress-bar
{
    background-color: #8A9A5B;
}

/* V2 PROFILE */
.show-profile .navbar /* PROFILE TAB */
{
    background-color: #505d42!important;
    border-top: 2px solid #505d42;
    border-bottom: 2px solid #505d42;
}
.show-profile .profile-account
{
    background-color: #F5F7F3;
}
.show-profile .navbar-avatar .navbar-avatar-container
