    background-color: #F5F7F3;
}
.sales .horse-name 
{
    font-weight: 600;
    color: #869970;
}
.topic-title a /* FORUM TAB */
{
    color: #869970;
}
.footer 
{
    background: #505D42;
}

/* V2 ESTATE */
.estate-item 
{
    background-color: #F5F7F3;
}

/* V2 NOTIFICATION */
a, a:active
{
    color: #869970;
}
a:hover
{
    color: #505D42;
}
nav .pagination .page-item.active .page-link 
{
    background: #869970;
}
nav .pagination .page-item.active .page-link:hover 
{
    background: #869970;
    color: #ffff;
}
nav .pagination .page-item .page-link:hover
{
   background: #F5F7F3;
}

