    background-color: #869970!important;
    border-color: #869970!important;
    color: #ffffff!important;
    /*border: none!important;*/
    box-shadow: none!important;
    outline: none!important;
}
.btn.btn-cta:hover,.btn.btn-primary:hover,.btn-warning:hover,#billingTypeButton:hover,.btn.btn-warning.btn-cta.btn-sm:hover,.btn.btn-primary.btn-darkblue.btn-sm:hover,.btn.btn-cta.btn-warning.btn-sm.single-add:hover,.btn.btn-cta.btn-warning.btn-sm.multi-add:hover,.btn.btn-warning.btn-cta.inline:hover,.btn.btn-primary.mb-3:hover,.btn.btn-warning.btn-cta:hover,.header-cta button:hover,.btn.btn-primary.btn-sm:hover,.btn.btn-danger:hover,.btn.btn-lg.btn-primary.btn-block:hover,.estate-item .estate-item-buy button:hover,.btn.btn-cta:active,.btn.btn-primary:active,.btn-warning:active,#billingTypeButton:active,.btn.btn-warning.btn-cta.btn-sm:active,.btn.btn-primary.btn-darkblue.btn-sm:active,.btn.btn-cta.btn-warning.btn-sm.single-add:active,.btn.btn-cta.btn-warning.btn-sm.multi-add:active,.btn.btn-warning.btn-cta.inline:active,.btn.btn-primary.mb-3:active,.btn.btn-warning.btn-cta:active,.header-cta button:active,.btn.btn-primary.btn-sm:active,.btn.btn-danger:active,.btn.btn-lg.btn-primary.btn-block:active,.estate-item .estate-item-buy button:active
