css:
@namespace url(http://www.w3.org/1999/xhtml);


/*Thread pages*/
   .logo {
      background: inherit ! important;
      color: #000000 ! important;
      font-size: 24pt ! important;
      font-weight: bold ! important;
      text-align: center ! important;
      clear: both ! important;
      width: 100% ! important;

   }

@-moz-document domain(zip.4chan.org),

domain(img.4chan.org),

domain(orz.4chan.org),

domain(boards.4chan.org),

domain(cgi.4chan.org) {


   blockquote{-moz-border-radius:20px !important;opacity:90 !important;background-color:rgb(0,0,0) !important;color:rgb(0,0,0)!important;

border:solid 0px rgb(0,0,0) !important;
margin-left:30px !important;
margin-right:5px !important;
margin-top:5px !important;
margin-bottom:15px !important;
padding-left:20px !important;
padding-right:20px !important;

}

   .omittedposts, .abbr {
      background: inherit ! important;
      color: #cd441f ! important;

   }

   body {
      background-color: #000000 ! important;
      color: #ffffff ! important;
      font-size: 18pt ! important;
      padding-left: 5px ! important;
      padding-right: 5px ! important;
      margin-right: 0px ! important;
      margin-left: 0px ! important;
      margin-top: 5px ! important;
    text-shadow: 0 0 0.2em #cd441f, 0 0 0.2em #cd441f,
        0 0 0.2em #cd441f; 
   }

body[bgcolor] { 

	background-image:url(http://img196.imageshack.us/img196/3686/1221086203798.jpg)!important;

	font: 100%/1.4 Helvetica, Arial, sans-serif !important;

	background-position:top left !important;

	background-repeat:no-repeat !important;

	background-attachment:fixed !important

	}

.reply { /* Reply posts */
background: #ffffff ! important;
color: #ffffff ! important;
font-family: serif ! important;
border: 0px solid #666666 ! important;
height: auto ! important

}
/*.filesize { font: 100%/1.4 Helvetica, Arial, sans-serif !important; }*/

a {

	color: #ffffff !important; 

    font-weight: 700 !important; 

	}



a:visited {

	font-weight: 200 !important;

    font-style: italic !important;

    opacity: 1.0 !important; 

	}



.logo {

	margin-bottom: 25px !important; 

    text-align: right !important;

    width: auto !important;

    margin: 0 30px 0 0 !important; 

	}



.logo img {

	float: left !important;

    clear: left !important;

	}



.logo font {

	margin: 0 !important; 

	}



.logo font b span, html > body > table > tbody > tr > th > font {

	color: #cd441f;

    font: 200 100%/1.4 Helvetica, Arial, sans-serif !important; 

	}



.logo font b span {

	color: #000000 !important;

    font-weight: 900 !important;

    font-size: 200% !important; 

	opacity: 0.70 !important; 

	}

html > body > table > tbody > tr > td,

html > body > table > tbody > tr > th

  { opacity: 0.50 !important;

    -moz-border-radius: 10px !important; }





html > body > table > tbody > tr > td { padding: 0.25em 0 !important; }



html > body > table > tbody > tr > td:hover, 

html > body > table > tbody > tr > td:focus,

html > body > table > tbody > tr > th:hover

  { opacity: 1.00 !important; }



.doubledash { display: none; }



.postarea > form > table > tbody > tr { opacity: 0.30 !important; }

.postarea > form > table > tbody > tr:hover { opacity: 1.00 !important; }



.postblock 

  { background-color: #000000!important;

    border-color: #cd441f!important;

    color: #000000 !important; 

    padding-top: 0.1850em !important;

    -moz-border-radius: 10px 0 0 10px !important;

    vertical-align: top !important;

    text-align: right !important; 
}



.inputtext, input[name="upfile"]

  { width: 100% !important;

    -moz-border-radius: 0 10px 10px 0 !important; }


.deletebuttons .inputtext {

 background-color: transparent !important;

	width: 100px !important; -moz-border-radius: 10px !important; 

	}



.pages {

	color: #000000 !important; 

    font-weight: 700 !important; 

	position: relative !important; 

	}



.pages a, .pages b {

	padding: 0 0.25em !important; 

	}



.pages a:visited {

	font-style: normal !important; 

	}



.pages b {

	color: #000000 !important; 

    font-weight: 700 !important; 

	position: relative !important; 

	font-size: 150% !important; margin-top: 0.25em !important;  

	font-style: italic !important; 

	}



.commentpostername a.linkmail[href="mailto:sage"]:after {

	content: "(ä¸‹ã’ã‚‹)"; 

	}



.filetitle {

	font-size: 125% !important;

    font-weight: 100 !important; 

    display: block;

    clear: right !important; 

	}



.reply, .replyhl, .pages {

	border: 1px solid #ff0000 !important;

	border-width: 1px 2px 2px 1px !important;

    border-style: solid !important; 

    -moz-border-radius-bottomright:12px !important;

	-moz-border-radius-topleft:12px !important;

    padding: 0 30px 20px 0 !important;

	background-color: transparent !important;

    opacity: 1.00 !important;

    }



.reply, .pages {

	background-color: transparent !important;

	border-color: #bababa !important; 

	}



.replyhl {

	background-color: transparent !important;

    border-color: #bababa !important;

    color: #5f5f5f !important; 

	}



.reply {

border: 2px solid #ffffff ! important;

background-image:url(http://img196.imageshack.us/img196/1732/animp.gif)!important;

background-color:#000000 !important;

background-position: top left !important;

background-repeat: repeat !important;

background-attachment: fixed !important;

-moz-border-radius-bottomright:12px !important;

-moz-border-radius-topleft:12px !important;

}

.replyhl {

border: 6px solid #ff0000 ! important;

background-image:url(http://img18.imageshack.us/img18/6505/anim2g.gif)!important;

background-color:#000000 !important;

background-position: top left !important;

background-repeat: repeat !important;

background-attachment: fixed !important;

-moz-border-radius-bottomright:12px !important;

-moz-border-radius-topleft:12px !important;

}



.pages {

	padding: 5px !important; 

	}



.postername, .commentpostername {

	color: #ffffff !important;
	font-size: 125% !important; 



	}



.postertrip {

	color: #ffffff !important; 

    opacity: 1.0 !important; 

	}



.replyhl .postertrip { 

	color: #ffffff !important; 

	}



.replyhl .commentpostername {

	color: #ff0000 !important; 

	}



.replyhl a {

	color: #ff0000 !important; 

	}



hr {

	border-color: #626262 !important; 

    width: 100% !important;

    clear: both !important; 

	}

/* 4chan extension */

DIV[class="4chan_ext_hiddenthreadinfo"] .reply {

	padding: 0 !important; 

    border: 0 none !important;

    -moz-border-radius: 0 !important;

    background-color: transparent !important; 

	}



.reply .postblock {

	text-align: center !important; 

	}



SPAN[class="4chan_win_header_buttons"] {

	right: 34px !important; 

	}



SPAN[style="border: thin solid black; padding: 3px; z-index: 100; position: absolute; right: 10px; top: 0px;"][class="reply"],

SPAN[style="border: thin solid black; padding: 3px; position: absolute; right: 10px;"][class="reply"] {

	padding: 0 !important;

    -moz-border-radius: 0 5px !important; 

	}



#twi_table_div {

	border: 0 none !important; 

    opacity: 0.90 !important; 

	}



#twi_table_div .postername,

#twi_table_div .postertrip,

#twi_table_div .filetitle {

	font-size: 100% !important; 

	}



#twi_table_div > .postblock {

	border-color: #FFF9F2 !important;

    border-width: 1px 1px 0 1px !important;

    -moz-border-radius: 10px 10px 0 0 !important;

    text-align: center !important; 

	}







#twi_table_div table .postblock {

	-moz-border-radius: 0 !important;

    background-color: transparent !important; 

    margin: 0 !important;

    color: #5f5f5f !important;

    text-align: center !important; 

	}



#twi_table_div > #twi_table {

	border-width: 0 1px 1px 1px !important;

    -moz-border-radius: 0 0 10px 10px !important; 

    padding: 0 0 5px 0 !important;

    opacity: 1.00 !important; 

	}

}

