css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://www.drgunn.net/ATA"), url-prefix("http://drgunn.net/ATA") {
	body { font-family:Calibri,Tahoma, Arial, sans-serif !important;}
	table, td, tr, thead, tfoot, tbody, th, tf { border-collapse: collapse; margin: 0px; padding: 0px; }
	table[align="center"] { margin-left: auto; margin-right: auto; } /* Fix for chrome center */
	table[width="90%"] { margin-left: auto; margin-right: auto; width: 95% !important; }
	td[width="242"] { width: 130px !important; vertical-align: top; padding-top: 2px; padding-bottom: 2px; }
	a, a:link, a:visited { text-decoration: none !important; color: teal !important; }
	a:hover { color: purple !important; }
	a img{ border: none !important; }
	td { background-color: #D1D1D1 !important; padding-right: 5px; padding-left: 5px; background-image: url(data:image/png;base64,); background-repeat: repeat-x; }
	td.LessonPlanHeaderLine[width="242"] p { display: none; }
	
	/*purple*/
	td[bgcolor="#440055"] { background-image: url(data:image/png;base64,); } 
	td[bgcolor="#440055"] a:link, td[bgcolor="#440055"] a:visited, td[bgcolor="#440055"] a { color: #f0f0f0 !important; }
	td[bgcolor="#440055"] a:hover { color: white !important; }
	/*magenta*/
	td[bgcolor="#FF00FF"] { background-image: url(data:image/png;base64,); background-color: #FF00FF !important; }
	td[bgcolor="#FF00FF"] a:link, td[bgcolor="#FF00FF"] a:visited, td[bgcolor="#FF00FF"] a { color: purple !important; }
	td[bgcolor="#FF00FF"] a:hover { color: teal !important; }
	td[bgcolor="#999999"] {background-image: url(data:image/png;base64,); background-color: #999 !important; }
	/*yellllow (pastel - darker)*/
	td[bgcolor="#FFFFCC"] , td[bgcolor="#FFFF99"] { background-color: #CBC800 !important; background-image: url(data:image/png;base64,) !important; }
	/*red (pastel - reg)*/
	td[bgcolor="#FF9999"], td[bgcolor="#FF0000"]  { background-color: #892403 !important;  background-image: url(data:image/png;base64,) !important; }  	
	td[bgcolor="#FF9999"] a:link, td[bgcolor="#FF0000"] a:link td[bgcolor="#FF9999"] a:visited, td[bgcolor="#FF0000"] a:visited  { color: pink !important;}
	td[bgcolor="#FF9999"] a:hover, td[bgcolor="#FF0000"] a:hover { color: orange !important; }
	/* Cyan */
	td[bgcolor="#00FFFF"] { background-color: #037B88 !important; background-image: url(data:image/png;base64,); }
    td[bgcolor="#00FFFF"] a:link, td[bgcolor="#00FFFF"] a:visited { color: purple !important; }
	td[bgcolor="#00FFFF"] a:hover { color: teal !important; }
	/*Green (pastel - light)*/
	td[bgcolor="#66FF99"], td[bgcolor="#00FF33"]  { background-color: #038927 !important; background-image: url(data:image/png;base64,);}
	/* Teal */
	td[bgcolor="#339999"] { background-image: url(data:image/png;base64,); background-color: #1B5352 !important; color: #fff !important; }
	td[bgcolor="#339999"] a:link, td[bgcolor="#339999"] a:visited { color: #f0f0f0 !important; }
	td[bgcolor="#339999"] a:hover { color: white !important; }	
	td.CurriculumLink a:link, td.CurriculumLink a:visited { color: black !important; }
	td.CurriculumLink a:hover { color: purple !important; }
	td table * { padding: 0px; } 
	table[width="36%"] { margin-top: 10px; margin-bottom: 10px; margin-left: auto; margin-right: auto;}
	table[width="36%"] td { border: 1px solid black !important; }
	table[width="36%"] tr:first-child td[colspan="8"] { font-size: 12pt !important; font-weight: normal !important; padding-top: 1px; padding-bottom: 1px; }
	table[width="36%"] td table tr td { border: none !important;}
	td img[src="Spacer28by25pixels.gif"] { height: 0px !important; width: 0px !important; margin-left: 28px; margin-top: 15px; }
	td.week, table[width="36%"] td:first-child  {background-image: url(data:image/png;base64,); background-color: #444 !important; color: #ccc !important}
	td.Weekend {color: purple !important; background-image: url(data:image/png;base64,); }
	table[width="36%"] td[bgcolor]:first-child { color: #000 !important; }
	a[href="http://www.atech.org"] { font-size: 16pt; font-family: Cambria, Georgia, Times, Times New Roman, serif !important; }
}
