css:
@-moz-document domain("clickcritters.com") {
:root {
  --offWhite: #f0f0f0;
  /* Blue */
  --accent: #5278c1;
  --darkerAccent: #3e598d;
  /* Purple */
/*   --accent: #806697;
  --darkerAccent: #634f75; */
  
  --mainBG: #313131;
  --lighterBG: #444;
  --darkerBG: #000;
  --errorBG: #cf6679;
  --successBG: #26b564;
  --adoptableBG: #959595;
  --sidebarBG: var(--darkerBG);
  
  --bodyText: #d3d3d3;
  --darkBodyText: #000;
  --errorText: var(--errorBG);
  --successText: var(--successBG);
  
  --link: var(--offWhite);
/*   --link: #eebe4a; */
/*   --link: #55cccc; */
  --button: var(--accent);
  --buttonText: var(--offWhite);
  --formField: var(--lighterBG);
  --tableBorder: #999;
  --forumPostBG: #ddd;
  --chatboxPostBG: var(--lighterBG);
  --chatboxPostText: var(--offWhite);
}

[style*="color: red"],
[style*="color:red"],
[color="red"] {
  color: var(--errorText) !important;
}

[style*="color: green"],
[style*="color:green"],
[color="green"] {
  color: var(--successText) !important;
}

body {
  background: linear-gradient(to bottom, var(--accent), var(--darkerBG));
  background-color: var(--darkerBG);
  background-repeat: repeat-x;
}

h1 {
  background-color: var(--accent);
  border: none;
  color: var(--offWhite);
}

h2,
h3,
h4,
h5,
h6 {
  color: var(--bodyText);
}

hr {
  background-color: var(--tableBorder);
}

input[type="text"]:not(#searchBoxInput),
input[type="email"],
input[type="password"],
input[type="number"],
input[type="file"],
textarea,
select {
  background: var(--formField) !important;
  border: none;
  color: var(--bodyText) !important;
}

input[type="submit"],
input[type="button"] {
  background: var(--button);
  color: var(--buttonText);
  padding: 8px;
}

table,
table tr,
table td {
  border-color: var(--tableBorder) !important;
}

#megaContent table {
  background: transparent !important;
}

.niceTable tr {
  border-bottom: 1px solid var(--tableBorder);
  border-left: none;
  border-right: none;
  border-top: 1px solid var(--tableBorder);
}

.niceTable tr:hover {
  background-color: var(--lighterBG);
}

.niceTable tr.headingRow {
  background-color: var(--lighterBG);
}

.niceTable tr.headingRow:hover {
  background-color: var(--lighterBG);
  border: none;
}

[class*=col] {
  background-color: unset;
}

#megaContent,
#megaFooter,
#megaBottomRight,
#megaAd {
  background-color: var(--mainBG);
  color: var(--bodyText);
}

#megaSidebar,
#megaBottomLeft {
  background-color: var(--sidebarBG);
}

#megaUserinfo {
  background: var(--darkerAccent);
  color: var(--bodyText);
}

#megaContent a:link,
#megaFooter a:link,
#megaUserinfo a:link,
#megaContent a:visited,
#megaFooter a:visited,
#megaUserinfo a:visited {
  color: var(--link);
}

a:link[style*="color: black"],
a:visited[style*="color: black"] {
  color: var(--link) !important;
}

/* Headings used in the cash shop */
.subHeading {
  background-color: transparent;
}

/* Green success messages */
#megaContent table[style*="solid green"],
#megaContent td[style*="solid green"] {
  background-color: var(--lighterBG) !important;
  border: 2px solid var(--successBG) !important;
  color: var(--bodyText);
}

/* Red error messages */
#megaContent table[style*="solid red"],
#megaContent td[style*="solid red"] {
  background-color: var(--lighterBG) !important;
  border: 2px solid var(--errorBG) !important;
  color: var(--bodyText);
}

/* Shop adopts/items, adopts in adopt selector pop-up */
.adopt,
.adoptBox {
  background: var(--adoptableBG);
  border: none;
  color: #000;
}

.adopt:hover {
  border: none;
}

/* Adoptable selector pop-up */
#cboxContent {
  background: var(--mainBG);
  color: var(--bodyText);
}

/* Adoptable guide, adopts in collection */
.adoptHave {
  background: linear-gradient(to bottom, var(--successBG), var(--darkerBG));
  border: 2px solid var(--successBG) !important;
  color: var(--bodyText);
}

/* Adoptable guide, adopts missing from collection */
.adoptMissing {
  background: linear-gradient(to bottom, var(--errorBG), var(--darkerBG));
  border: 2px solid var(--errorBG) !important;
  color: var(--bodyText);
}

.adoptHave a,
.adoptMissing a {
  color: inherit !important;
}

/* Inventory item info */
.infoBox {
  background: transparent;
  border: 1px solid var(--tableBorder);
}

/* Adoptable images */
#megaContent img[src*="images/adoptables"],
#megaContent img[src*="images/species"] {
  background-color: var(--adoptableBG);
}

.folder_link {
  background-color: var(--mainBG);
  border-color: var(--tableBorder);
}

#selected_folder {
  background-color: var(--lighterBG);
  border-color: var(--tableBorder);
}

/* White chat box posts */
.shoutboxtext[style*="background: white"] {
  background-color: var(--chatboxPostBG) !important;
  color: var(--chatboxPostText);
}

/* White chat box posts' usernames */
.shoutboxtext[style*="background: white"] a {
  color: var(--link);
}

/* Forum categories */
.forum {
  background: var(--lighterBG);
  border-radius: 10px 0 10px 0;
  color: var(--bodyText);
}

.forum div {
  background: none;
}

/* Forum category heading */
.category {
  background-color: var(--accent);
  position: relative;
}

/* Override forum category heading triangle image */
.category div {
  background: var(--mainBG);
}

/* Forum category heading pseudo triangle */
.category div::after {
  border-bottom: 10px solid transparent;
  border-left: 10px solid var(--accent);
  border-right: 10px solid transparent;
  border-top: 10px solid var(--accent);
  content: '';
  display: inline-block;
  height: 0;
  left: 8px;
  position: absolute;
  width: 0;
}

/* Unread forum posts */
tr.unread {
  background-color: var(--mainBG); 
}

/* Read forum posts */
tr.off {
  background-color: var(--lighterBG); 
}

/* Forum row hover color */
tr.on {
  background-color: var(--lighterBG);
}

/* PM heading */
#mail_heading {
  background-color: var(--lighterBG) !important;
}

/* Countdown timer */
.hasCountdown {
  background-color: var(--lighterBG);
  border: none;
}

/* Colored input fields next to BC images (shop stock, TS new trade/offer) */
img[src*="images/bc/"] + input[type="text"] {
  color: var(--darkBodyText);
}

/* Colored boxes, Traptor Nests stock treats page */
.blueBox,
.greenBox,
.purpleBox,
.redBox,
.yellowBox,
.blackBox {
  color: var(--darkBodyText);
}

/* Fetch task */
#letter-div {
  color: var(--darkBodyText);
}
}

@-moz-document url-prefix("https://www.clickcritters.com/clickexchange.php") {
/* ==================================================================
   Click Exchange
===================================================================== */


table {
  color: inherit;
}

/* CE body */
div[style*="background-color: white"] {
  background-color: var(--mainBG) !important;
  color: var(--bodyText);
}

/* CE links */
div[style*="background-color: white"] a:link,
div[style*="background-color: white"] a:visited {
  color: var(--link);
}

/* CE prizes */
table[style*="background: white"] {
  background-color: var(--lighterBG) !important;
}
table[style*="solid green"] {
  background-color: var(--lighterBG) !important;
  border: 2px solid var(--successBG) !important;
  color: var(--bodyText);
}

/* Adoptable images */
img[src*="images/adoptables"],
img[src*="images/cepics"] {
  background-color: var(--adoptableBG);
}
}

@-moz-document url-prefix("https://www.clickcritters.com/forum/view_topic.php") {
/* ==================================================================
   Forum Threads
===================================================================== */

/* Forum post content */
a[href*="forum/view_topic.php"] ~ table tr td:nth-child(2) {
  background-color: var(--forumPostBG);
  color: var(--darkBodyText);
}
/* Links */
#megaContent a[href*="forum/view_topic.php"] ~ table tr td:nth-child(2) a:link,
#megaContent a[href*="forum/view_topic.php"] ~ table tr td:nth-child(2) a:visited {
  color: var(--darkBodyText);
}

/* Topics with only one page */
a[href*="view_forum.php"] ~ table tr td:nth-child(2) {
  background-color: var(--forumPostBG);
  color: var(--darkBodyText);
}
/* Links */
#megaContent a[href*="view_forum.php"] ~ table tr td:nth-child(2) a:link,
#megaContent a[href*="view_forum.php"] ~ table tr td:nth-child(2) a:visited {
  color: var(--darkBodyText);
}
}

@-moz-document url-prefix("https://www.clickcritters.com/tradestation.php") {
/* ==================================================================
   Trade Station
===================================================================== */

a[href*="act=search&type=tradeid"] ~ div {
  background-color: var(--lighterBG);
  border: none;
}

a[href*="act=search&type=tradeid"] ~ div div {
  background-color: unset !important;
}
}

@-moz-document url-prefix("https://www.clickcritters.com/uchi_fusion.php") {
/* ==================================================================
   Uchi Fusion
===================================================================== */

td[style*="background:"] {
  color: var(--darkBodyText);
}
}

@-moz-document url-prefix("https://www.clickcritters.com/lost.php") {
/* ==================================================================
   Lost
===================================================================== */

#megaContent table table td {
  border: 1px solid var(--lighterBG) !important;
}

#megaContent table table td[style*="background: #CCCCCC"] {
  background: var(--accent) !important;
}

/* Walls */
#megaContent table table td[style*="border-top: 1px solid black"] {
  border-top: 1px solid #fff !important;
}
#megaContent table table td[style*="border-right: 1px solid black"] {
  border-right: 1px solid #fff !important;
}
#megaContent table table td[style*="border-bottom: 1px solid black"] {
  border-bottom: 1px solid #fff !important;
}
#megaContent table table td[style*="border-left: 1px solid black"] {
  border-left: 1px solid #fff !important;
}

/* Open */
#megaContent table table td[style*="border-top: 1px solid #CCCCCC"] {
  border-top: 1px solid var(--accent) !important;
}
#megaContent table table td[style*="border-right: 1px solid #CCCCCC"] {
  border-right: 1px solid var(--accent) !important;
}
#megaContent table table td[style*="border-bottom: 1px solid #CCCCCC"] {
  border-bottom: 1px solid var(--accent) !important;
}
#megaContent table table td[style*="border-left: 1px solid #CCCCCC"] {
  border-left: 1px solid var(--accent) !important;
}
}

@-moz-document url("https://www.clickcritters.com/friends.php") {
/* ==================================================================
   Friends List
===================================================================== */

/* Friends list table rows */
#megaContent table:first-of-type tr td {
  background-color: var(--mainBG) !important;
  border-bottom: 1px solid var(--tableBorder);
}

/* Friends list heading row */
#megaContent table:first-of-type tr:first-of-type td {
  background-color: var(--accent) !important;
}
}
