css:
@-moz-document domain("app.roll20.net") {
#radial-menu .markermenu.open {
    width: 320px !important;
}

#radial-menu .markermenu.open {
    top: 66px !important;
}

#radial-menu .markermenu .markericon {
    width: 60px !important;
    padding-left: 28px !important;
    font-size: 1em !important;
}

#radial-menu .markermenu .markercolor {
    width: 60px !important;
    padding-left: 28px !important;
    font-size: 1em !important;
    color: #fff !important;
    font-weight: bold;
}

#radial-menu .markermenu .markercolor.dead {
    background-color: #CC1010 !important;
    color: #fff !important;
    top: 0px !important;
    border-radius: 20px;
    margin-right: 2px;
    margin-left: 2px;
    margin-top: 5px;
}

div[data-action-type=toggle_status_dead]::after {
    content: " Mark" !important;
}

div[data-action-type=toggle_status_blue]::after {
    content: "Blue" !important;
}
