css:
@-moz-document url-prefix("https://skribbl.io/") {
:root {
    --BackgroundDark: #2d2d2d;
    --GlobalText: #9a9a9a;
    --UsernameColour: /*[[KE03L]]*/
}

body {
    background: url(https://i.imgur.com/yOdF2BQ.png) center fixed;
    background-size: auto;
    background-size: 256px;
}

.loginPanelContent {
    background-color: var(--BackgroundDark);
}

.informationTabs a {
    color: var(--GlobalText);;
}

.panel-group, .col-xs-12, #modalIdle {
    display: none;
}

#overlay .revealReason {
    color: #a8a8a8;
}


.containerTools .toolActive {
    border: 1px dashed #ffffffbd !important;
}

.containerTools .tool,
.containerBrushSizes .brushSize {
    background-color: #00000069;
}
.containerTools .tool:hover,
.containerBrushSizes .brushSize:hover {
    background-color: #00000087;
}
.containerBrushSizes .size {
    background-color: #686868;
}
.containerBrushSizes .size:focus {
    background-color: #a7a7a7 !important;
}
.containerTools {
    background-color: transparent;
}
#overlay .content .wordContainer .word {
    background-color: #b6b6b6;
}

.btn.disabled, .btn[disabled], fieldset[disabled] .btn {
    cursor: not-allowed !important;
}

.updateInfo, 
.lobbySettings .lobbyContent,
.lobbySettings .lobbyName {
    background-color: #2d2d2d;
    border-color: #1c1c1c;
    color: var(--GlobalText);
}

.modal-content {
    background-color: #2d2d2d;
    border-color: #1c1c1c;
    color: var(--GlobalText);
}
.modal-header {
    background: #262626;
    border-bottom: 1px solid #ffffff1c !important;
}
.modal-footer {
    border-top: 0px solid !important;
}
button.close {
    color: white;
    text-shadow: none;
    transition: .2s
}


#containerChat, #screenGame .gameHeader {
    background-color: #2d2d2d;
    border: 1px solid #1c1c1c;
    color: var(--GlobalText);
}

#invite, .invite-overlay {
    background: #2d2d2d;
    border-color: #1c1c1c;
    color: var(--GlobalText);
}

#loginAvatarCustomizeContainer {
    background-color: #252525;
    border-color: #1c1c1c;
}

.form-control, #loginLanguage, #formChat {
    color: #ffffff7d;
    background-color: #252525;
    border-color: #1c1c1c;
    box-shadow: none !important;
}

.form-control:focus {
    color: #c9c9c9;
    border-color: #000000ab !important;
}

.btn-success, .btn-warning, #inviteCopyButton,
.btn-success:active, .btn-warning:active, #inviteCopyButton:active {
    background-color: #1d1d1d !important;
    border-color: #00000057 !important;
    border-radius: 3px;
    transition: .3s;
}

.btn-success:hover, .btn-warning:hover, #inviteCopyButton:hover {
    background-color: #1c1c1cba !important;
    border-color: #38c952 !important;
    border-style: dashed;
}

.btn-info {
    color: #f2f2f2;
    background-color: #58585852;
    border-color: #0000008c;
    width: 50%;
    margin: auto;
    border-radius: 3px;
    transition: .3s;
}

.btn-primary {
    color: #f2f2f2;
    background-color: #1e1e1edb;
    border-color: #0000008c;
    width: 50%;
    margin: auto;
    border-radius: 3px;
    transition: .3s;
}

.btn-primary:hover {
    background-color: #252525;
    border-color: #35b8e7;
    border-style: dashed;
}


.btn-info:hover {
    background-color: #49494952;
    border-color: #35b8e7;
    border-style: dashed;
}

.avatarArrow {
    filter: grayscale(100%);
}

.avatarArrow:active {
    filter: brightness(50%) grayscale(100%);
}

a {
    color: grey !important;
}

#boxMessages, #containerGamePlayers .player:nth-child(2n+1) {
    background-color: #2d2d2d;
}
#boxMessages p:nth-child(2n), #containerGamePlayers .player:nth-child(2n) {
    background-color: #2a2a2a;
}

.timer-container {
    filter: brightness(50%);
}
#timer {
    color: #000 !important;
}

#containerGamePlayers .player .info .score {
    padding-top: 1.2px;
    color: #b4b4b4 !important;
}

.name[style*='color: rgb(0, 0, 255);'] {
    color: grey;
}

#containerGamePlayers .player .rank {
    color: #7f7f7f7a;
}

#containerGamePlayers .player .info .name {
    color: #ffffffeb;
}

[style*='color: rgb(0, 0, 0);'] > b {
    color: #6d6d6d !important;
}
[style*='color: rgb(0, 0, 0);'] > span {
    color: #b4b4b4 !important;
}
[style*='color: rgb(86, 206, 39); font-weight: bold;'] > span,
div.guessedWord > div:nth-child(2) > div:nth-child(1) {
    color: #33bc4b !important;
}
[style*='color: rgb(125, 173, 63);'] > b {
    color: #619f6c !important;
}
[style*='color: rgb(125, 173, 63);'] > span {
    color: #9fcaa7 !important;
}

[style*='color: rgb(0, 0, 255);'] {
    color: var(--UsernameColour) !important;
}

[style*='color: rgb(57, 117, 206); font-weight: bold;'] > span {
    color: #5b83d0 !important;
}
[style*='color: rgb(204, 204, 0); font-weight: bold;'] > span {
    color: #c93838 !important;
}
[style*='color: rgb(206, 79, 10); font-weight: bold;'] {
    color: #bfbfbf !important;
}
}
